export enum AttendanceStatus {
  PRESENT = 'Present',
  ABSENT = 'Absent',
  OD = 'OD',
  NOT_MARKED = 'Not Marked'
}

export enum LeadStatus {
  NEW = 'New',
  CONTACTED = 'Contacted',
  CONVERTED = 'Converted'
}

export interface Student {
  id: string;
  rollNo: string;
  name: string;
  department: string;
  year: number;
  phone: string;
  status: 'Active' | 'Inactive';
  attendancePercentage: number;
}

export interface AttendanceRecord {
  id: string;
  date: string;
  studentId: string;
  status: AttendanceStatus;
  subject: string;
  class: string;
  remark?: string;
}

export interface Lead {
  id: string;
  name: string;
  phone: string;
  course: string;
  status: LeadStatus;
  nextFollowUp: string;
  notes: string[];
}

export interface ChatMessage {
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
}

