import { GoogleGenAI, Type } from "@google/genai";
import { Student, AttendanceRecord, Lead } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const getAttendanceSummary = async (attendance: AttendanceRecord[], students: Student[]) => {
  const model = ai.models.generateContent({
    model: 'gemini-3-flash-preview',
    contents: `Analyze this attendance data and provide a concise human-readable summary.
    Data: ${JSON.stringify(attendance)}
    Students: ${JSON.stringify(students)}
    Focus on trends, low attendance alerts, and key absentees.`,
  });
  const response = await model;
  return response.text;
};

export const getAIAssistantResponse = async (query: string, context: { students: Student[], attendance: AttendanceRecord[], leads: Lead[] }) => {
  const model = ai.models.generateContent({
    model: 'gemini-3-flash-preview',
    contents: `You are EduManage AI, an intelligent assistant for a student management system.
    Current Context:
    Students: ${JSON.stringify(context.students)}
    Attendance Today: ${JSON.stringify(context.attendance.filter(a => a.date === new Date().toISOString().split('T')[0]))}
    Leads: ${JSON.stringify(context.leads)}

    Answer the following query based on this data: "${query}"
    Be professional, helpful, and concise.`,
  });
  const response = await model;
  return response.text;
};

export const generateLeadFollowup = async (lead: Lead) => {
  const model = ai.models.generateContent({
    model: 'gemini-3-flash-preview',
    contents: `Generate a polite and professional follow-up message for a potential student.
    Lead Name: ${lead.name}
    Course: ${lead.course}
    Status: ${lead.status}
    Context: ${lead.notes.join('. ')}`,
  });
  const response = await model;
  return response.text;
};

