import React, { useState, useEffect } from 'react';
import { 
  Calendar, 
  ChevronLeft, 
  ChevronRight, 
  CheckCircle2, 
  XCircle, 
  Clock, 
  Search,
  Check
} from 'lucide-react';
import { Student, AttendanceStatus, AttendanceRecord } from '../types';
import { CLASSES, SUBJECTS } from '../constants';

interface AttendanceProps {
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const Attendance: React.FC<AttendanceProps> = ({ students, onSave }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedClass, setSelectedClass] = useState(CLASSES[1]); // CSE-2
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [marking, setMarking] = useState<Record<string, AttendanceStatus>>({});
  const [showSavedToast, setShowSavedToast] = useState(false);

  // Initialize markings with 'Present' as default for easier UX
  useEffect(() => {
    const initial: Record<string, AttendanceStatus> = {};
    students.forEach(s => {
      initial[s.id] = AttendanceStatus.PRESENT;
    });
    setMarking(initial);
  }, [students]);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setMarking(prev => ({ ...prev, [studentId]: status }));
  };

  const handleMarkAll = (status: AttendanceStatus) => {
    const next = { ...marking };
    students.forEach(s => next[s.id] = status);
    setMarking(next);
  };

  const handleSubmit = () => {
    const records: AttendanceRecord[] = students.map(s => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: s.id,
      date: selectedDate,
      status: marking[s.id] || AttendanceStatus.PRESENT,
      class: selectedClass,
      subject: selectedSubject,
    }));
    onSave(records);
    setShowSavedToast(true);
    setTimeout(() => setShowSavedToast(false), 3000);
  };

  const filteredStudents = students.filter(s => 
    s.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    s.rollNo.includes(searchTerm)
  );

  return (
    <div className="p-8 max-w-6xl mx-auto space-y-6 relative">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Daily Attendance</h1>
          <p className="text-slate-500 mt-1">Marking attendance for {selectedClass} | {new Date(selectedDate).toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}</p>
        </div>
        <div className="flex gap-3">
          <button 
            onClick={() => handleMarkAll(AttendanceStatus.PRESENT)}
            className="px-4 py-2 bg-white border border-slate-200 rounded-lg text-slate-700 font-medium hover:bg-slate-50 transition-colors"
          >
            Mark All Present
          </button>
          <button 
            onClick={handleSubmit}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg font-bold hover:bg-blue-700 shadow-lg shadow-blue-500/20 transition-all flex items-center gap-2"
          >
            Submit Attendance
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <div className="bg-white p-4 rounded-xl border border-slate-100 shadow-sm">
          <label className="block text-xs font-bold text-slate-400 uppercase tracking-wider mb-2">Select Date</label>
          <input 
            type="date" 
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="w-full bg-slate-50 border-none rounded-lg text-sm p-2.5 outline-none font-medium" 
          />
        </div>
        <div className="bg-white p-4 rounded-xl border border-slate-100 shadow-sm">
          <label className="block text-xs font-bold text-slate-400 uppercase tracking-wider mb-2">Select Class</label>
          <select 
            value={selectedClass}
            onChange={(e) => setSelectedClass(e.target.value)}
            className="w-full bg-slate-50 border-none rounded-lg text-sm p-2.5 outline-none font-medium appearance-none"
          >
            {CLASSES.map(c => <option key={c} value={c}>{c}</option>)}
          </select>
        </div>
        <div className="bg-white p-4 rounded-xl border border-slate-100 shadow-sm">
          <label className="block text-xs font-bold text-slate-400 uppercase tracking-wider mb-2">Subject</label>
          <select 
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            className="w-full bg-slate-50 border-none rounded-lg text-sm p-2.5 outline-none font-medium appearance-none"
          >
            {SUBJECTS.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
        <div className="bg-white p-4 rounded-xl border border-slate-100 shadow-sm">
          <label className="block text-xs font-bold text-slate-400 uppercase tracking-wider mb-2">Search Student</label>
          <div className="relative">
            <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-400" />
            <input 
              type="text" 
              placeholder="Roll No or Name"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-lg text-sm p-2.5 pl-10 outline-none font-medium" 
            />
          </div>
        </div>
      </div>

      <div className="bg-white rounded-2xl border border-slate-100 shadow-sm overflow-hidden">
        <table className="w-full text-left">
          <thead className="bg-slate-50/50 border-b border-slate-100">
            <tr>
              <th className="px-6 py-4 text-xs font-bold text-slate-400 uppercase tracking-wider">Roll No</th>
              <th className="px-6 py-4 text-xs font-bold text-slate-400 uppercase tracking-wider">Student</th>
              <th className="px-6 py-4 text-xs font-bold text-slate-400 uppercase tracking-wider text-center">Status</th>
              <th className="px-6 py-4 text-xs font-bold text-slate-400 uppercase tracking-wider text-center">Remark</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-slate-100">
            {filteredStudents.map((student) => (
              <tr key={student.id} className="hover:bg-slate-50/50 transition-colors group">
                <td className="px-6 py-4">
                  <span className="text-sm font-semibold text-slate-500">{student.rollNo}</span>
                </td>
                <td className="px-6 py-4">
                  <div className="flex items-center gap-3">
                    <div className="w-9 h-9 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center font-bold text-xs">
                      {student.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <span className="font-semibold text-slate-800">{student.name}</span>
                  </div>
                </td>
                <td className="px-6 py-4">
                  <div className="flex justify-center items-center gap-2">
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                      className={`px-4 py-2 rounded-lg text-xs font-bold transition-all ${
                        marking[student.id] === AttendanceStatus.PRESENT 
                          ? 'bg-emerald-100 text-emerald-700 shadow-sm ring-1 ring-emerald-200' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      Present
                    </button>
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                      className={`px-4 py-2 rounded-lg text-xs font-bold transition-all ${
                        marking[student.id] === AttendanceStatus.ABSENT 
                          ? 'bg-rose-100 text-rose-700 shadow-sm ring-1 ring-rose-200' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      Absent
                    </button>
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                      className={`px-4 py-2 rounded-lg text-xs font-bold transition-all ${
                        marking[student.id] === AttendanceStatus.OD 
                          ? 'bg-blue-100 text-blue-700 shadow-sm ring-1 ring-blue-200' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      OD
                    </button>
                  </div>
                </td>
                <td className="px-6 py-4 text-center">
                  <input 
                    type="text" 
                    placeholder="â€”"
                    className="bg-transparent border-none text-xs text-slate-500 text-center focus:ring-0 placeholder-slate-300 italic w-full"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showSavedToast && (
        <div className="fixed bottom-10 left-1/2 -translate-x-1/2 bg-slate-900 text-white px-6 py-4 rounded-2xl shadow-2xl flex items-center gap-4 animate-in fade-in slide-in-from-bottom-4">
          <div className="w-8 h-8 rounded-full bg-emerald-500 flex items-center justify-center">
            <Check size={18} className="text-white" />
          </div>
          <div>
            <p className="font-bold text-sm">Attendance saved successfully</p>
            <p className="text-xs text-slate-400">All changes have been synced with the database.</p>
          </div>
          <button onClick={() => setShowSavedToast(false)} className="ml-4 text-slate-500 hover:text-white">
            <XCircle size={20} />
          </button>
        </div>
      )}
    </div>
  );
};

export default Attendance;

import React, { useState } from 'react';
import { 
  Plus, 
  Search, 
  Filter, 
  Phone, 
  Mail, 
  Calendar as CalendarIcon,
  MoreVertical,
  CheckCircle2,
  ExternalLink,
  MessageSquarePlus,
  ArrowRight
} from 'lucide-react';
import { Lead, LeadStatus } from '../types';
import { generateLeadFollowup } from '../services/geminiService';

interface LeadsProps {
  leads: Lead[];
  onAdd: (lead: Omit<Lead, 'id'>) => void;
  onUpdate: (id: string, updates: Partial<Lead>) => void;
}

const Leads: React.FC<LeadsProps> = ({ leads, onAdd, onUpdate }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [aiLoading, setAiLoading] = useState<string | null>(null);
  const [aiMessage, setAiMessage] = useState<string | null>(null);

  const getStatusColor = (status: LeadStatus) => {
    switch (status) {
      case LeadStatus.NEW: return 'bg-blue-50 text-blue-600 border-blue-100';
      case LeadStatus.CONTACTED: return 'bg-amber-50 text-amber-600 border-amber-100';
      case LeadStatus.CONVERTED: return 'bg-emerald-50 text-emerald-600 border-emerald-100';
    }
  };

  const handleGenFollowup = async (lead: Lead) => {
    setAiLoading(lead.id);
    try {
      const msg = await generateLeadFollowup(lead);
      setAiMessage(msg || 'Failed to generate message');
    } catch (e) {
      setAiMessage('Error generating AI message');
    } finally {
      setAiLoading(null);
    }
  };

  const filteredLeads = leads.filter(l => 
    l.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    l.course.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-8 space-y-8">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Leads Management</h1>
          <p className="text-slate-500 mt-1">Track and manage student enquiries and admissions</p>
        </div>
        <button className="flex items-center gap-2 px-6 py-2.5 bg-blue-600 text-white rounded-xl font-bold hover:bg-blue-700 shadow-lg shadow-blue-500/20 transition-all">
          <Plus size={20} />
          New Enquiry
        </button>
      </div>

      <div className="flex gap-4">
        <div className="relative flex-1">
          <Search size={20} className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" />
          <input 
            type="text" 
            placeholder="Search by name, course or phone..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full bg-white border border-slate-200 rounded-xl py-3 pl-12 pr-4 outline-none focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 transition-all"
          />
        </div>
        <button className="flex items-center gap-2 px-6 py-3 bg-white border border-slate-200 rounded-xl text-slate-600 font-medium hover:bg-slate-50 transition-colors">
          <Filter size={18} />
          Filter
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
        {filteredLeads.map((lead) => (
          <div key={lead.id} className="bg-white rounded-2xl border border-slate-100 shadow-sm hover:shadow-md transition-all group overflow-hidden">
            <div className="p-6 space-y-4">
              <div className="flex justify-between items-start">
                <div className="flex items-center gap-3">
                  <div className="w-12 h-12 rounded-xl bg-slate-100 flex items-center justify-center text-slate-400 font-bold text-lg">
                    {lead.name.charAt(0)}
                  </div>
                  <div>
                    <h3 className="font-bold text-slate-900 leading-tight">{lead.name}</h3>
                    <p className="text-xs text-slate-500">{lead.course} Enquiry</p>
                  </div>
                </div>
                <div className={`px-3 py-1 rounded-full text-[10px] font-bold uppercase tracking-wider border ${getStatusColor(lead.status)}`}>
                  {lead.status}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4 py-2 border-y border-slate-50">
                <div className="flex items-center gap-2 text-slate-500">
                  <Phone size={14} />
                  <span className="text-xs font-medium">{lead.phone}</span>
                </div>
                <div className="flex items-center gap-2 text-slate-500">
                  <CalendarIcon size={14} />
                  <span className="text-xs font-medium">{lead.nextFollowUp}</span>
                </div>
              </div>

              <div className="space-y-3">
                <div className="flex items-center justify-between text-xs">
                  <span className="text-slate-400 font-medium">Recent Activity</span>
                  <button className="text-blue-600 font-bold hover:underline flex items-center gap-1">
                    Details <ArrowRight size={10} />
                  </button>
                </div>
                <div className="p-3 bg-slate-50 rounded-xl">
                  <p className="text-xs text-slate-600 line-clamp-2 italic">
                    {lead.notes.length > 0 ? lead.notes[0] : "No recent notes added."}
                  </p>
                </div>
              </div>
            </div>

            <div className="p-3 bg-slate-50 flex gap-2">
              <button 
                onClick={() => handleGenFollowup(lead)}
                disabled={!!aiLoading}
                className="flex-1 flex items-center justify-center gap-2 py-2 bg-white border border-slate-200 rounded-lg text-xs font-bold text-slate-700 hover:bg-slate-100 transition-all disabled:opacity-50"
              >
                {aiLoading === lead.id ? (
                  <div className="w-4 h-4 border-2 border-slate-300 border-t-blue-600 rounded-full animate-spin"></div>
                ) : (
                  <MessageSquarePlus size={14} className="text-blue-500" />
                )}
                AI Follow-up
              </button>
              <button className="p-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                <ExternalLink size={16} />
              </button>
            </div>
          </div>
        ))}
      </div>

      {aiMessage && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[60] flex items-center justify-center p-4">
          <div className="bg-white w-full max-w-lg rounded-3xl shadow-2xl overflow-hidden animate-in zoom-in-95 duration-200">
            <div className="p-6 bg-blue-600 text-white flex justify-between items-center">
              <div className="flex items-center gap-3">
                <Sparkles size={24} />
                <h3 className="text-xl font-bold">AI Follow-up Suggestion</h3>
              </div>
              <button onClick={() => setAiMessage(null)} className="p-1 hover:bg-blue-500 rounded-lg">
                <XCircle size={24} />
              </button>
            </div>
            <div className="p-8">
              <div className="bg-slate-50 p-6 rounded-2xl border border-slate-100 mb-6">
                <p className="text-slate-700 leading-relaxed text-sm whitespace-pre-wrap">{aiMessage}</p>
              </div>
              <div className="flex gap-3">
                <button 
                  onClick={() => {
                    navigator.clipboard.writeText(aiMessage);
                    setAiMessage(null);
                  }}
                  className="flex-1 py-3 bg-slate-900 text-white rounded-xl font-bold hover:bg-slate-800 transition-all"
                >
                  Copy to Clipboard
                </button>
                <button 
                  onClick={() => setAiMessage(null)}
