import React, { useState, useRef, useEffect } from 'react';
import { Send, Sparkles, User, Bot, Loader2, Info, XCircle } from 'lucide-react';
import { getAIAssistantResponse } from '../services/geminiService';
import { Student, AttendanceRecord, ChatMessage } from '../types';

interface AIAssistantProps {
  students: Student[];
  attendance: AttendanceRecord[];
}

const AIAssistant: React.FC<AIAssistantProps> = ({ students, attendance }) => {
  const [messages, setMessages] = useState<ChatMessage[]>([
    { role: 'assistant', content: "Hello! I'm your EduManage AI. I can analyze attendance trends, identify frequent absentees, and provide academic insights. Try asking: 'Who had low attendance this week?'", timestamp: new Date() }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMsg: ChatMessage = { role: 'user', content: input, timestamp: new Date() };
    setMessages(prev => [...prev, userMsg]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await getAIAssistantResponse(input, { students, attendance });
      const assistantMsg: ChatMessage = { role: 'assistant', content: response || "Analysis complete, but I couldn't formulate a specific response.", timestamp: new Date() };
      setMessages(prev => [...prev, assistantMsg]);
    } catch (error) {
      setMessages(prev => [...prev, { role: 'assistant', content: "Connection to analysis engine interrupted. Please verify your connection.", timestamp: new Date() }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="h-[calc(100vh-84px)] p-10 flex flex-col max-w-5xl mx-auto space-y-8 animate-in fade-in duration-700">
      <div className="flex items-center gap-6">
        <div className="p-4 bg-indigo-600 rounded-[1.5rem] shadow-2xl shadow-indigo-600/30">
          <Sparkles className="text-white" size={32} />
        </div>
        <div>
          <h1 className="text-4xl font-black text-slate-900">Academic Insights</h1>
          <p className="text-slate-500 font-medium">AI-powered data interpretation and trend analysis</p>
        </div>
      </div>

      <div className="flex-1 bg-white rounded-[3rem] border border-slate-100 shadow-2xl shadow-slate-200/50 overflow-hidden flex flex-col relative">
        <div className="bg-slate-50/70 p-5 flex items-center gap-4 border-b border-slate-100/50 backdrop-blur-sm">
          <div className="w-2.5 h-2.5 rounded-full bg-emerald-500 animate-pulse ring-4 ring-emerald-500/10"></div>
          <span className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Neural Engine Active</span>
          <div className="ml-auto">
            <button className="text-[10px] font-black text-indigo-600 uppercase tracking-widest hover:text-indigo-700 transition-colors flex items-center gap-2">
              <Info size={14} /> View Capabilities
            </button>
          </div>
        </div>

        <div ref={scrollRef} className="flex-1 overflow-y-auto p-10 space-y-8 custom-scrollbar">
          {messages.map((msg, idx) => (
            <div key={idx} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-[85%] flex gap-6 ${msg.role === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                <div className={`w-12 h-12 shrink-0 rounded-2xl flex items-center justify-center shadow-md ${
                  msg.role === 'user' ? 'bg-slate-900 text-white' : 'bg-indigo-50 text-indigo-600 border-2 border-indigo-100'
                }`}>
                  {msg.role === 'user' ? <User size={24} /> : <Bot size={24} />}
                </div>
                <div className={`p-6 rounded-[2rem] ${
                  msg.role === 'user' 
                    ? 'bg-indigo-600 text-white rounded-tr-none shadow-xl shadow-indigo-600/20' 
                    : 'bg-slate-50 text-slate-800 rounded-tl-none border border-slate-100'
                }`}>
                  <p className="text-sm font-semibold leading-relaxed whitespace-pre-wrap">{msg.content}</p>
                  <span className={`text-[9px] mt-4 block font-black uppercase tracking-widest opacity-40 ${msg.role === 'user' ? 'text-right' : 'text-left'}`}>
                    {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                </div>
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="flex justify-start">
              <div className="flex gap-6">
                <div className="w-12 h-12 rounded-2xl bg-indigo-50 border-2 border-indigo-100 flex items-center justify-center text-indigo-600">
                  <Loader2 className="animate-spin" size={24} />
                </div>
                <div className="bg-slate-50 p-6 rounded-[2rem] rounded-tl-none border border-slate-100 flex items-center gap-2">
                  <div className="w-1.5 h-1.5 bg-indigo-300 rounded-full animate-bounce" style={{animationDelay: '0ms'}}></div>
                  <div className="w-1.5 h-1.5 bg-indigo-400 rounded-full animate-bounce" style={{animationDelay: '200ms'}}></div>
                  <div className="w-1.5 h-1.5 bg-indigo-500 rounded-full animate-bounce" style={{animationDelay: '400ms'}}></div>
                </div>
              </div>
            </div>
          )}
        </div>

        <div className="p-8 border-t border-slate-100 bg-white shadow-[0_-15px_30px_-15px_rgba(0,0,0,0.05)]">
          <div className="flex items-center gap-4 bg-slate-50 p-3 pl-6 rounded-3xl focus-within:ring-4 focus-within:ring-indigo-500/10 transition-all border border-slate-100">
            <input 
              type="text" 
              placeholder="Query student records or patterns..." 
              className="flex-1 bg-transparent border-none outline-none text-sm font-bold text-slate-700 py-3"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            />
            <button 
              onClick={handleSend}
              disabled={isLoading || !input.trim()}
              className="bg-indigo-600 text-white p-4 rounded-2xl hover:bg-indigo-700 disabled:opacity-50 shadow-xl shadow-indigo-600/30 transition-all active:scale-95"
            >
              <Send size={24} />
            </button>
          </div>
          <div className="flex gap-3 mt-6 overflow-x-auto pb-4 custom-scrollbar">
            {["Who is absent today?", "Attendance below 75%", "Monthly trend report", "Subject performance"].map((hint) => (
              <button 
                key={hint}
                onClick={() => setInput(hint)}
                className="whitespace-nowrap px-4 py-2 bg-white border border-slate-200 rounded-2xl text-[10px] font-black uppercase tracking-widest text-slate-400 hover:border-indigo-400 hover:text-indigo-600 hover:bg-indigo-50 transition-all shadow-sm"
              >
                {hint}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

