import React, { useState, useMemo } from 'react';
import { 
  Folder, 
  ChevronRight, 
  Calendar, 
  FileText, 
  Clock, 
  ChevronDown, 
  ArrowLeft,
  UserCheck,
  UserX,
  History as HistoryIcon,
  Search
} from 'lucide-react';
import { AttendanceRecord, Student } from '../types';

interface HistoryProps {
  records: AttendanceRecord[];
  students: Student[];
}

const HistoryPage: React.FC<HistoryProps> = ({ records, students }) => {
  const [selectedFolder, setSelectedFolder] = useState<string | null>(null);
  const [selectedSession, setSelectedSession] = useState<string | null>(null);

  // Group records by Date (Folders)
  const dateFolders = useMemo(() => {
    const folders: Record<string, AttendanceRecord[]> = {};
    records.forEach(r => {
      if (!folders[r.date]) folders[r.date] = [];
      folders[r.date].push(r);
    });
    return folders;
  }, [records]);

  // Group sessions within a selected date
  const sessionsInFolder = useMemo(() => {
    if (!selectedFolder) return {};
    const sessionMap: Record<string, AttendanceRecord[]> = {};
    dateFolders[selectedFolder].forEach(r => {
      if (!sessionMap[r.timestamp]) sessionMap[r.timestamp] = [];
      sessionMap[r.timestamp].push(r);
    });
    return sessionMap;
  }, [selectedFolder, dateFolders]);

  const currentSessionRecords = useMemo(() => {
    if (!selectedSession || !selectedFolder) return [];
    return sessionsInFolder[selectedSession];
  }, [selectedSession, sessionsInFolder]);

  if (records.length === 0) {
    return (
      <div className="p-20 text-center flex flex-col items-center gap-6">
        <div className="w-24 h-24 bg-slate-100 rounded-full flex items-center justify-center text-slate-300">
          <HistoryIcon size={48} />
        </div>
        <div>
          <h2 className="text-2xl font-black text-slate-900">No History Records</h2>
          <p className="text-slate-500 mt-2 font-medium">Take your first attendance to see folders here.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-10 max-w-6xl mx-auto space-y-8 animate-in fade-in duration-500">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-4xl font-black text-slate-900 tracking-tight">System Logs</h1>
          <p className="text-slate-500 mt-2 font-medium">Chronological record of campus activity</p>
        </div>
        {selectedFolder && (
          <button 
            onClick={() => { setSelectedFolder(null); setSelectedSession(null); }}
            className="flex items-center gap-2 px-6 py-3 bg-white border border-slate-200 rounded-2xl text-slate-600 font-bold hover:bg-slate-50 transition-all shadow-sm"
          >
            <ArrowLeft size={18} />
            Back to Folders
          </button>
        )}
      </div>

      {!selectedFolder ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {Object.keys(dateFolders).sort().reverse().map(date => (
            <button 
              key={date}
              onClick={() => setSelectedFolder(date)}
              className="bg-white p-6 rounded-[2rem] border-2 border-slate-50 shadow-sm hover:shadow-xl hover:border-indigo-100 transition-all text-left group"
            >
              <div className="w-14 h-14 bg-indigo-50 text-indigo-500 rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform">
                <Folder size={28} fill="currentColor" fillOpacity={0.1} />
              </div>
              <p className="text-sm font-black text-slate-900 mb-1">{new Date(date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
              <p className="text-xs text-slate-400 font-bold uppercase tracking-wider">
                {Array.from(new Set(dateFolders[date].map(r => r.timestamp))).length} Session Folders
              </p>
            </button>
          ))}
        </div>
      ) : !selectedSession ? (
        <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden">
          <div className="p-8 border-b border-slate-50 bg-slate-50/50">
            <h2 className="text-xl font-black text-slate-900 flex items-center gap-3">
              <Calendar size={20} className="text-indigo-500" />
              Folder: {selectedFolder}
            </h2>
          </div>
          <div className="divide-y divide-slate-50">
            {Object.keys(sessionsInFolder).map(sessionTime => (
              <button 
                key={sessionTime}
                onClick={() => setSelectedSession(sessionTime)}
                className="w-full flex items-center justify-between p-8 hover:bg-indigo-50/30 transition-all text-left group"
              >
                <div className="flex items-center gap-6">
                  <div className="p-4 bg-white border border-slate-100 rounded-2xl text-slate-400 group-hover:text-indigo-500 transition-colors">
                    <FileText size={24} />
                  </div>
                  <div>
                    <p className="font-black text-slate-900 text-lg">{sessionsInFolder[sessionTime][0].subject}</p>
                    <p className="text-xs text-slate-400 font-bold uppercase tracking-widest mt-1">
                      {sessionTime} â€¢ {sessionsInFolder[sessionTime][0].class}
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <span className="bg-emerald-50 text-emerald-600 px-4 py-1.5 rounded-full text-xs font-black">
                    {sessionsInFolder[sessionTime].filter(r => r.status === 'Present').length} Present
                  </span>
                  <ChevronRight size={20} className="text-slate-300 group-hover:translate-x-1 transition-transform" />
                </div>
              </button>
            ))}
          </div>
        </div>
      ) : (
        <div className="space-y-8 animate-in slide-in-from-right-10 duration-500">
          <div className="bg-white p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
            <div className="flex items-center gap-6">
              <button 
                onClick={() => setSelectedSession(null)}
                className="p-3 bg-slate-100 text-slate-500 rounded-2xl hover:bg-slate-200 transition-colors"
              >
                <ArrowLeft size={20} />
              </button>
              <div>
                <h3 className="text-2xl font-black text-slate-900">{currentSessionRecords[0].subject}</h3>
                <p className="text-sm font-bold text-slate-400 mt-1">{selectedFolder} â€¢ {selectedSession}</p>
              </div>
            </div>
            <div className="flex gap-4">
              <div className="px-4 py-2 bg-emerald-50 text-emerald-600 rounded-xl flex items-center gap-2 border border-emerald-100">
                <UserCheck size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Present').length}</span>
              </div>
              <div className="px-4 py-2 bg-rose-50 text-rose-600 rounded-xl flex items-center gap-2 border border-rose-100">
                <UserX size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Absent').length}</span>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden">
            <table className="w-full text-left">
              <thead className="bg-slate-50/70 border-b border-slate-100">
                <tr>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest">Student</th>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest">Reg No</th>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest text-center">Status</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-slate-50">
                {currentSessionRecords.map(record => {
                  const student = students.find(s => s.id === record.studentId);
                  return (
                    <tr key={record.id} className="hover:bg-slate-50/50 transition-all">
                      <td className="px-8 py-6">
                        <div className="flex items-center gap-4">
                          <div className="w-10 h-10 rounded-xl bg-slate-100 flex items-center justify-center font-black text-xs text-slate-400">
                            {student?.name.split(' ').map(n => n[0]).join('')}
                          </div>
                          <span className="font-bold text-slate-900">{student?.name}</span>
                        </div>
                      </td>
                      <td className="px-8 py-6">
                        <span className="text-xs font-bold text-slate-400">{student?.rollNo}</span>
                      </td>
                      <td className="px-8 py-6 text-center">
                        <span className={`px-4 py-1.5 rounded-full text-[10px] font-black uppercase tracking-wider ${
                          record.status === 'Present' ? 'bg-emerald-100 text-emerald-600' :
                          record.status === 'Absent' ? 'bg-rose-100 text-rose-600' :
                          'bg-sky-100 text-sky-600'
                        }`}>
                          {record.status}
                        </span>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default HistoryPage;

import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Dashboard from './pages/Dashboard';
import Attendance from './pages/Attendance';
import Students from './pages/Students';
import AIAssistant from './pages/AIAssistant';
import HistoryPage from './pages/History'; // New History view
import { MOCK_STUDENTS } from './constants';
import { Student, AttendanceRecord } from './types';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);

  const handleSaveAttendance = (newRecords: AttendanceRecord[]) => {
    setAttendanceRecords(prev => [...prev, ...newRecords]);
  };

  const handleAddStudent = (student: Omit<Student, 'id' | 'attendancePercentage'>) => {
    const freshStudent: Student = {
      ...student,
      id: Math.random().toString(36).substr(2, 9),
      attendancePercentage: 0
    };
    setStudents(prev => [freshStudent, ...prev]);
  };

  const handleDeleteStudent = (id: string) => {
    setStudents(prev => prev.filter(s => s.id !== id));
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard 
          students={students} 
          attendance={attendanceRecords} 
          onTakeAttendance={() => setActiveTab('attendance')} 
        />;
      case 'attendance':
        return <Attendance students={students} onSave={handleSaveAttendance} />;
      case 'history':
        return <HistoryPage records={attendanceRecords} students={students} />;
      case 'students':
        return <Students 
          students={students} 
          onAdd={handleAddStudent} 
          onDelete={handleDeleteStudent} 
        />;
      case 'assistant':
        return <AIAssistant students={students} attendance={attendanceRecords} />;
      case 'settings':
        return (
          <div className="p-8">
            <h1 className="text-4xl font-black text-slate-900 tracking-tight">System Settings</h1>
            <p className="text-slate-500 mt-2 font-medium italic">General administrative configuration for campus folders.</p>
          </div>
        );
      default:
        return <Dashboard 
          students={students} 
          attendance={attendanceRecords} 
          onTakeAttendance={() => setActiveTab('attendance')} 
        />;
    }
  };

  return (
    <div className="flex min-h-screen bg-slate-50 selection:bg-indigo-100 selection:text-indigo-900">
      <Sidebar activeTab={activeTab} setActiveTab={setActiveTab} />
      <main className="flex-1 ml-64 min-h-screen">
        <header className="sticky top-0 z-40 bg-white/70 backdrop-blur-xl border-b border-slate-100/50 px-10 py-5 flex items-center justify-between">
          <div className="relative w-[28rem]">
            <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-slate-300">
              <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
              </svg>
            </div>
            <input 
              type="text" 
              placeholder="Search global campus data..." 
              className="block w-full pl-12 pr-4 py-3 border-none bg-slate-100/50 rounded-[1.25rem] text-sm font-bold placeholder-slate-400 focus:outline-none focus:ring-4 focus:ring-indigo-500/10 focus:bg-white transition-all shadow-sm"
            />
          </div>
          <div className="flex items-center gap-6">
            {/* Notification bell removed as requested */}
            <div className="h-10 w-[1.5px] bg-slate-100"></div>
            <div className="flex items-center gap-4 bg-slate-50 py-2 pl-2 pr-4 rounded-[1.25rem] border border-transparent hover:border-slate-200 transition-all">
              <div className="w-10 h-10 rounded-full bg-indigo-100 flex items-center justify-center text-indigo-600 font-black text-xs border border-indigo-200 shadow-sm">
                CA
              </div>
              <div className="hidden sm:block">
                <p className="text-xs font-black text-slate-900 uppercase tracking-tighter">Campus Admin</p>
                <p className="text-[10px] text-emerald-500 font-black tracking-widest uppercase">Verified</p>
              </div>
            </div>
          </div>
        </header>

        <div className="animate-in fade-in slide-in-from-top-4 duration-1000">
          {renderContent()}
        </div>
      </main>
    </div>
  );
};

