import React, { useState, useMemo } from 'react';
import { 
  Folder, 
  ChevronRight, 
  Calendar, 
  FileText, 
  Clock, 
  ChevronDown, 
  ArrowLeft,
  UserCheck,
  UserX,
  History as HistoryIcon,
  Search
} from 'lucide-react';
import { AttendanceRecord, Student } from '../types';

interface HistoryProps {
  records: AttendanceRecord[];
  students: Student[];
}

const HistoryPage: React.FC<HistoryProps> = ({ records, students }) => {
  const [selectedFolder, setSelectedFolder] = useState<string | null>(null);
  const [selectedSession, setSelectedSession] = useState<string | null>(null);

  const dateFolders = useMemo(() => {
    const folders: Record<string, AttendanceRecord[]> = {};
    records.forEach(r => {
      if (!folders[r.date]) folders[r.date] = [];
      folders[r.date].push(r);
    });
    return folders;
  }, [records]);

  const sessionsInFolder = useMemo(() => {
    if (!selectedFolder) return {};
    const sessionMap: Record<string, AttendanceRecord[]> = {};
    dateFolders[selectedFolder].forEach(r => {
      if (!sessionMap[r.timestamp]) sessionMap[r.timestamp] = [];
      sessionMap[r.timestamp].push(r);
    });
    return sessionMap;
  }, [selectedFolder, dateFolders]);

  const currentSessionRecords = useMemo(() => {
    if (!selectedSession || !selectedFolder) return [];
    return sessionsInFolder[selectedSession];
  }, [selectedSession, sessionsInFolder]);

  if (records.length === 0) {
    return (
      <div className="p-10 lg:p-20 text-center flex flex-col items-center gap-6">
        <div className="w-20 lg:w-24 h-20 lg:h-24 bg-slate-100 dark:bg-slate-900 rounded-full flex items-center justify-center text-slate-300 dark:text-slate-800">
          <HistoryIcon size={48} />
        </div>
        <div>
          <h2 className="text-xl lg:text-2xl font-black text-slate-900 dark:text-white">No Logs Found</h2>
          <p className="text-slate-500 dark:text-slate-400 mt-2 font-medium">Recorded sessions will appear as folders here.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 lg:p-10 max-w-6xl mx-auto space-y-8 animate-in fade-in duration-500">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900 dark:text-white tracking-tight">Campus Logs</h1>
          <p className="text-slate-500 dark:text-slate-400 mt-2 font-medium">Browsing automated attendance folders</p>
        </div>
        {selectedFolder && (
          <button 
            onClick={() => { setSelectedFolder(null); setSelectedSession(null); }}
            className="flex items-center justify-center gap-2 px-6 py-3 bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-800 rounded-2xl text-slate-600 dark:text-slate-400 font-bold hover:bg-slate-50 dark:hover:bg-slate-800 transition-all shadow-sm"
          >
            <ArrowLeft size={18} />
            Folders
          </button>
        )}
      </div>

      {!selectedFolder ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {Object.keys(dateFolders).sort().reverse().map(date => (
            <button 
              key={date}
              onClick={() => setSelectedFolder(date)}
              className="bg-white dark:bg-slate-900 p-6 rounded-[2rem] border-2 border-slate-50 dark:border-slate-800 shadow-sm hover:shadow-xl dark:hover:shadow-indigo-500/5 hover:border-indigo-100 dark:hover:border-indigo-900/50 transition-all text-left group"
            >
              <div className="w-14 h-14 bg-indigo-50 dark:bg-indigo-900/20 text-indigo-500 dark:text-indigo-400 rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform">
                <Folder size={28} fill="currentColor" fillOpacity={0.1} />
              </div>
              <p className="text-sm font-black text-slate-900 dark:text-white mb-1">{new Date(date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
              <p className="text-xs text-slate-400 dark:text-slate-500 font-bold uppercase tracking-wider">
                {Array.from(new Set(dateFolders[date].map(r => r.timestamp))).length} Sessions
              </p>
            </button>
          ))}
        </div>
      ) : !selectedSession ? (
        <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] border border-slate-100 dark:border-slate-800 shadow-sm overflow-hidden">
          <div className="p-6 lg:p-8 border-b border-slate-50 dark:border-slate-800 bg-slate-50/50 dark:bg-slate-800/20">
            <h2 className="text-lg lg:text-xl font-black text-slate-900 dark:text-white flex items-center gap-3">
              <Calendar size={20} className="text-indigo-500 dark:text-indigo-400" />
              Folder: {selectedFolder}
            </h2>
          </div>
          <div className="divide-y divide-slate-50 dark:divide-slate-800">
            {Object.keys(sessionsInFolder).map(sessionTime => (
              <button 
                key={sessionTime}
                onClick={() => setSelectedSession(sessionTime)}
                className="w-full flex items-center justify-between p-6 lg:p-8 hover:bg-indigo-50/30 dark:hover:bg-indigo-900/10 transition-all text-left group"
              >
                <div className="flex items-center gap-4 lg:gap-6">
                  <div className="p-4 bg-white dark:bg-slate-800 border border-slate-100 dark:border-slate-700 rounded-2xl text-slate-400 dark:text-slate-600 group-hover:text-indigo-500 dark:group-hover:text-indigo-400 transition-colors">
                    <FileText size={24} />
                  </div>
                  <div>
                    <p className="font-black text-slate-900 dark:text-white text-base lg:text-lg leading-tight">{sessionsInFolder[sessionTime][0].subject}</p>
                    <p className="text-[10px] lg:text-xs text-slate-400 dark:text-slate-500 font-bold uppercase tracking-widest mt-1">
                      {sessionTime} â€¢ {sessionsInFolder[sessionTime][0].class}
                    </p>
                  </div>
                </div>
                <div className="flex items-center gap-4">
                  <span className="bg-emerald-50 dark:bg-emerald-900/20 text-emerald-600 dark:text-emerald-400 px-3 lg:px-4 py-1 lg:py-1.5 rounded-full text-[10px] lg:text-xs font-black hidden sm:block">
                    {sessionsInFolder[sessionTime].filter(r => r.status === 'Present').length} P
                  </span>
                  <ChevronRight size={20} className="text-slate-300 dark:text-slate-700 group-hover:translate-x-1 transition-transform" />
                </div>
              </button>
            ))}
          </div>
        </div>
      ) : (
        <div className="space-y-6 lg:space-y-8 animate-in slide-in-from-right-10 duration-500">
          <div className="bg-white dark:bg-slate-900 p-6 lg:p-8 rounded-[2.5rem] border border-slate-100 dark:border-slate-800 shadow-sm flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
            <div className="flex items-center gap-4 lg:gap-6">
              <button 
                onClick={() => setSelectedSession(null)}
                className="p-3 bg-slate-100 dark:bg-slate-800 text-slate-500 dark:text-slate-400 rounded-2xl hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
              >
                <ArrowLeft size={20} />
              </button>
              <div>
                <h3 className="text-xl lg:text-2xl font-black text-slate-900 dark:text-white leading-tight">{currentSessionRecords[0].subject}</h3>
                <p className="text-xs lg:text-sm font-bold text-slate-400 dark:text-slate-500 mt-1">{selectedFolder} â€¢ {selectedSession}</p>
              </div>
            </div>
            <div className="flex gap-4">
              <div className="px-4 py-2 bg-emerald-50 dark:bg-emerald-900/20 text-emerald-600 dark:text-emerald-400 rounded-xl flex items-center gap-2 border border-emerald-100 dark:border-emerald-800">
                <UserCheck size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Present').length}</span>
              </div>
              <div className="px-4 py-2 bg-rose-50 dark:bg-rose-900/20 text-rose-600 dark:text-rose-400 rounded-xl flex items-center gap-2 border border-rose-100 dark:border-rose-800">
                <UserX size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Absent').length}</span>
              </div>
            </div>
          </div>

          <div className="bg-white dark:bg-slate-900 rounded-[2.5rem] border border-slate-100 dark:border-slate-800 shadow-sm overflow-x-auto">
            <table className="w-full text-left min-w-[500px]">
              <thead className="bg-slate-50/70 dark:bg-slate-800/50 border-b border-slate-100 dark:border-slate-800">
                <tr>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 dark:text-slate-500 uppercase tracking-widest">Student</th>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 dark:text-slate-500 uppercase tracking-widest">Reg No</th>
                  <th className="px-8 py-5 text-[11px] font-black text-slate-400 dark:text-slate-500 uppercase tracking-widest text-center">Status</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-slate-50 dark:divide-slate-800">
                {currentSessionRecords.map(record => {
                  const student = students.find(s => s.id === record.studentId);
                  return (
                    <tr key={record.id} className="hover:bg-slate-50/50 dark:hover:bg-slate-800/50 transition-all">
                      <td className="px-8 py-6">
                        <div className="flex items-center gap-4">
                          <div className="w-10 h-10 rounded-xl bg-slate-100 dark:bg-slate-800 flex items-center justify-center font-black text-xs text-slate-400 dark:text-slate-600">
                            {student?.name.split(' ').map(n => n[0]).join('')}
                          </div>
                          <span className="font-bold text-slate-900 dark:text-white">{student?.name}</span>
                        </div>
                      </td>
                      <td className="px-8 py-6">
                        <span className="text-xs font-bold text-slate-400 dark:text-slate-600">{student?.rollNo}</span>
                      </td>
                      <td className="px-8 py-6 text-center">
                        <span className={`px-4 py-1.5 rounded-full text-[10px] font-black uppercase tracking-wider ${
                          record.status === 'Present' ? 'bg-emerald-100 dark:bg-emerald-900/20 text-emerald-600 dark:text-emerald-400' :
                          record.status === 'Absent' ? 'bg-rose-100 dark:bg-rose-900/20 text-rose-600 dark:text-rose-400' :
                          'bg-sky-100 dark:bg-sky-900/20 text-sky-600 dark:text-sky-400'
                        }`}>
                          {record.status}
                        </span>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default HistoryPage;

import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Dashboard from './pages/Dashboard';
import Attendance from './pages/Attendance';
import Students from './pages/Students';
import HistoryPage from './pages/History';
import { MOCK_STUDENTS } from './constants';
import { Student, AttendanceRecord } from './types';
import { Menu } from 'lucide-react';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const handleSaveAttendance = (newRecords: AttendanceRecord[]) => {
    setAttendanceRecords(prev => [...prev, ...newRecords]);
  };

  const handleAddStudent = (student: Omit<Student, 'id' | 'attendancePercentage'>) => {
    const freshStudent: Student = {
      ...student,
      id: Math.random().toString(36).substr(2, 9),
      attendancePercentage: 0
    };
    setStudents(prev => [freshStudent, ...prev]);
  };

  const handleDeleteStudent = (id: string) => {
    setStudents(prev => prev.filter(s => s.id !== id));
  };

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard 
          students={students} 
          attendance={attendanceRecords} 
          onTakeAttendance={() => setActiveTab('attendance')} 
        />;
      case 'attendance':
        return <Attendance students={students} onSave={handleSaveAttendance} />;
      case 'history':
        return <HistoryPage records={attendanceRecords} students={students} />;
      case 'students':
        return <Students 
          students={students} 
          onAdd={handleAddStudent} 
          onDelete={handleDeleteStudent} 
        />;
      default:
        return <Dashboard 
          students={students} 
          attendance={attendanceRecords} 
          onTakeAttendance={() => setActiveTab('attendance')} 
        />;
    }
  };

  return (
    <div className="min-h-screen bg-slate-50 flex">
      {/* Overlay for mobile sidebar */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-40 lg:hidden backdrop-blur-sm"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      <div className={`fixed inset-y-0 left-0 z-50 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0 transition-transform duration-300 ease-in-out`}>
        <Sidebar 
          activeTab={activeTab} 
          setActiveTab={(tab) => {
            setActiveTab(tab);
            setIsSidebarOpen(false);
          }}
        />
      </div>

      <main className="flex-1 lg:ml-64 min-h-screen flex flex-col">
        <header className="sticky top-0 z-30 bg-white/70 backdrop-blur-xl border-b border-slate-200 px-4 lg:px-10 py-4 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => setIsSidebarOpen(true)}
              className="lg:hidden p-2 text-slate-500 hover:bg-slate-100 rounded-xl transition-colors"
            >
              <Menu size={24} />
            </button>
            <div className="relative hidden md:block w-64 lg:w-96">
              <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-slate-400">
                <svg className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2.5} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
              <input 
                type="text" 
                placeholder="Search global campus data..." 
                className="block w-full pl-12 pr-4 py-2.5 border-none bg-slate-100/50 rounded-2xl text-sm font-bold placeholder-slate-400 focus:outline-none focus:ring-4 focus:ring-indigo-500/10 focus:bg-white transition-all shadow-sm"
              />
            </div>
          </div>

          <div className="flex items-center gap-3 lg:gap-6">
            <div className="h-10 w-[1.5px] bg-slate-200 hidden sm:block"></div>
            <div className="flex items-center gap-3 bg-slate-50 py-1.5 pl-1.5 pr-3 rounded-2xl border border-transparent">
              <div className="w-8 lg:w-10 h-8 lg:h-10 rounded-full bg-indigo-100 flex items-center justify-center text-indigo-600 font-black text-xs border border-indigo-200 shadow-sm">
                CA
              </div>
              <div className="hidden sm:block">
                <p className="text-[10px] lg:text-xs font-black text-slate-900 uppercase tracking-tighter">Admin</p>
                <p className="text-[8px] lg:text-[10px] text-emerald-500 font-black tracking-widest uppercase">Verified</p>
              </div>
            </div>
          </div>
        </header>

        <div className="flex-1 overflow-x-hidden">
          <div className="animate-in fade-in slide-in-from-top-4 duration-700">
            {renderContent()}
          </div>
        </div>
      </main>
    </div>
  );
};

export default App;

