} from 'recharts';
import { Student, AttendanceRecord } from '../types';

interface DashboardProps {
  students: Student[];
  attendance: AttendanceRecord[];
  onTakeAttendance: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ students, attendance, onTakeAttendance }) => {
  const stats = useMemo(() => {
    const today = new Date().toISOString().split('T')[0];
    const todaysAttendance = attendance.filter(a => a.date === today);
    const presentToday = todaysAttendance.filter(a => a.status === 'Present').length;
    const odToday = todaysAttendance.filter(a => a.status === 'OD').length;

    return {
      totalStudents: students.length,
      attendancePercentage: todaysAttendance.length > 0 
        ? Math.round((presentToday / todaysAttendance.length) * 100) 
        : 88,
      absentToday: todaysAttendance.filter(a => a.status === 'Absent').length,
      odToday: odToday,
      departmentsCount: Array.from(new Set(students.map(s => s.department))).length
    };
  }, [students, attendance]);

  const chartData = [
    { name: 'Mon', present: 88 },
    { name: 'Tue', present: 92 },
    { name: 'Wed', present: 85 },
    { name: 'Thu', present: 78 },
    { name: 'Fri', present: 82 },
    { name: 'Sat', present: 90 },
  ];

  const StatCard = ({ title, value, icon: Icon, color, trend }: any) => (
    <div className="bg-white p-6 rounded-3xl border border-slate-100 shadow-sm hover:shadow-xl transition-all duration-300 group">
      <div className="flex justify-between items-start mb-4">
        <div className={`p-4 rounded-2xl bg-${color}-50 text-${color}-600 group-hover:scale-110 transition-transform`}>
          <Icon size={24} />
        </div>
        {trend && (
          <div className="flex items-center gap-1 text-emerald-600 text-sm font-bold bg-emerald-50 px-2 py-1 rounded-full">
            <TrendingUp size={14} />
            {trend}
          </div>
        )}
      </div>
      <h3 className="text-slate-500 text-xs font-bold uppercase tracking-widest">{title}</h3>
      <p className="text-3xl font-black text-slate-900 mt-1">{value}</p>
    </div>
  );

  return (
    <div className="p-4 lg:p-8 space-y-8 animate-in fade-in slide-in-from-bottom-6 duration-700">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tight">Campus Overview</h1>
          <p className="text-slate-500 mt-2 font-medium">Monitoring real-time attendance & session folders</p>
        </div>
        <div className="flex gap-4">
          <button 
            onClick={onTakeAttendance}
            className="w-full md:w-auto flex items-center justify-center gap-2 px-8 py-3.5 bg-indigo-600 text-white rounded-2xl font-bold hover:bg-indigo-700 shadow-xl shadow-indigo-600/30 transition-all hover:-translate-y-1 active:scale-95"
          >
            <CalendarCheck size={22} />
            Take Attendance
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4 lg:gap-6">
        <StatCard title="Total Students" value={stats.totalStudents} icon={Users} color="indigo" trend="+4" />
        <StatCard title="Today's Avg %" value={`${stats.attendancePercentage}%`} icon={TrendingUp} color="emerald" trend="+1.2%" />
        <StatCard title="Absent Today" value={stats.absentToday} icon={UserMinus} color="rose" />
        <StatCard title="OD Today" value={stats.odToday} icon={Clock} color="sky" />
        <StatCard title="Departments" value={stats.departmentsCount} icon={LayoutGrid} color="amber" />
      </div>

      <div className="bg-white p-6 lg:p-8 rounded-[2rem] border border-slate-100 shadow-sm">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 mb-10">
          <div>
            <h2 className="text-xl font-black text-slate-900">Attendance Analytics</h2>
            <p className="text-sm font-medium text-slate-400">Weekly campus performance trend</p>
          </div>
          <select className="bg-slate-50 border-none rounded-xl text-xs font-bold p-3 outline-none cursor-pointer ring-1 ring-slate-100">
            <option>This Week</option>
            <option>Last Week</option>
          </select>
        </div>
        <div className="h-[250px] lg:h-[350px]">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={chartData}>
              <defs>
                <linearGradient id="colorInd" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#4f46e5" stopOpacity={0.15}/>
                  <stop offset="95%" stopColor="#4f46e5" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f1f5f9" />
              <XAxis 
                dataKey="name" 
                axisLine={false} 
                tickLine={false} 
                tick={{fill: '#94a3b8', fontSize: 12, fontWeight: 600}} 
                dy={15} 
              />
              <YAxis 
                axisLine={false} 
                tickLine={false} 
                tick={{fill: '#94a3b8', fontSize: 12, fontWeight: 600}} 
              />
              <Tooltip 
                contentStyle={{ 
                  borderRadius: '20px', 
                  border: 'none', 
                  boxShadow: '0 25px 50px -12px rgb(0 0 0 / 0.15)', 
                  padding: '12px',
                  backgroundColor: '#0f172a',
                  color: '#fff'
                }}
              />
              <Area type="monotone" dataKey="present" stroke="#4f46e5" strokeWidth={4} fillOpacity={1} fill="url(#colorInd)" />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState, useEffect } from 'react';
import { 
  Search,
  Check,
  XCircle,
  FileText,
  Clock,
  UserCheck,
  UserX,
  Zap,
  ChevronDown,
  Layers
} from 'lucide-react';
import { Student, AttendanceStatus, AttendanceRecord } from '../types';
import { CLASSES, ELECTIVES } from '../constants';

interface AttendanceProps {
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const Attendance: React.FC<AttendanceProps> = ({ students, onSave }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedClass, setSelectedClass] = useState(CLASSES[1]);
  const [selectedElective, setSelectedElective] = useState(ELECTIVES[0]);
  const [period, setPeriod] = useState('1st Period');
  const [searchTerm, setSearchTerm] = useState('');
  const [marking, setMarking] = useState<Record<string, AttendanceStatus>>({});
  const [showSavedToast, setShowSavedToast] = useState(false);
  const [lastSummary, setLastSummary] = useState<{
    present: string[],
    absent: string[],
    od: string[]
  } | null>(null);

  useEffect(() => {
    const initial: Record<string, AttendanceStatus> = {};
    students.forEach(s => {
      initial[s.id] = AttendanceStatus.PRESENT;
    });
    setMarking(initial);
  }, [students]);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setMarking(prev => ({ ...prev, [studentId]: status }));
  };

  const handleMarkAll = (status: AttendanceStatus) => {
    const next = { ...marking };
    students.forEach(s => next[s.id] = status);
    setMarking(next);
  };

  const handleSubmit = () => {
    const now = new Date();
    const timestamp = now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });

    const records: AttendanceRecord[] = students.map(s => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: s.id,
      date: selectedDate,
      timestamp: `${period} - ${timestamp}`,
      status: marking[s.id] || AttendanceStatus.PRESENT,
      class: selectedClass,
      subject: selectedElective,
    }));

    const summary = {
      present: students.filter(s => marking[s.id] === AttendanceStatus.PRESENT).map(s => s.name),
      absent: students.filter(s => marking[s.id] === AttendanceStatus.ABSENT).map(s => s.name),
      od: students.filter(s => marking[s.id] === AttendanceStatus.OD).map(s => s.name)
    };

    onSave(records);
    setLastSummary(summary);
    setShowSavedToast(true);
    window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' });
    setTimeout(() => setShowSavedToast(false), 3000);
  };

  const filteredStudents = students.filter(s => 
    s.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    s.rollNo.includes(searchTerm)
  );

  return (
    <div className="p-4 lg:p-8 max-w-7xl mx-auto space-y-8 pb-32">
      <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900">Attendance Desk</h1>
          <p className="text-slate-500 mt-2 font-medium italic">Create session folders for campus records</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-4">
          <button 
            onClick={() => handleMarkAll(AttendanceStatus.PRESENT)}
            className="px-6 py-3 bg-white border-2 border-slate-100 rounded-2xl text-slate-600 font-bold hover:border-indigo-100 hover:bg-slate-50 transition-all flex items-center justify-center gap-2"
          >
            <UserCheck size={18} />
            Bulk Present
          </button>
          <button 
            onClick={handleSubmit}
            className="px-10 py-3.5 bg-slate-900 text-white rounded-2xl font-bold hover:bg-slate-800 shadow-2xl shadow-indigo-600/20 transition-all flex items-center justify-center gap-3"
          >
            <Zap size={20} className="fill-indigo-500 text-indigo-500" />
            Commit Record
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-5 gap-4">
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3">Folder Date</label>
          <input 
            type="date" 
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="w-full bg-slate-50 border-none rounded-xl text-sm p-3 outline-none font-bold text-slate-700" 
          />
        </div>
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3">Period/Session</label>
          <div className="relative">
            <select 
              value={period}
              onChange={(e) => setPeriod(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-xl text-sm p-3 outline-none font-bold text-slate-700 appearance-none"
            >
              <option>1st Period</option>
              <option>2nd Period</option>
              <option>3rd Period</option>
              <option>4th Period</option>
              <option>Special Session</option>
            </select>
            <Layers size={14} className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 pointer-events-none" />
          </div>
        </div>
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3">Subject</label>
          <select 
            value={selectedElective}
            onChange={(e) => setSelectedElective(e.target.value)}
            className="w-full bg-indigo-50/50 border-none rounded-xl text-sm p-3 outline-none font-bold text-indigo-700 appearance-none"
          >
            {ELECTIVES.map(s => <option key={s} value={s}>{s}</option>)}
          </select>
        </div>
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3">Target Class</label>
          <select 
            value={selectedClass}
            onChange={(e) => setSelectedClass(e.target.value)}
            className="w-full bg-slate-50 border-none rounded-xl text-sm p-3 outline-none font-bold text-slate-700 appearance-none"
          >
            {CLASSES.map(c => <option key={c} value={c}>{c}</option>)}
          </select>
        </div>
        <div className="bg-white p-5 rounded-3xl border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3">Quick Filter</label>
          <div className="relative">
            <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-slate-300" />
            <input 
              type="text" 
              placeholder="Lookup..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-xl text-sm p-3 pl-10 outline-none font-bold text-slate-700" 
            />
          </div>
        </div>
      </div>

      <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-x-auto">
        <table className="w-full text-left min-w-[600px]">
          <thead className="bg-slate-50/70 border-b border-slate-100">
            <tr>
              <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest">Register No</th>
              <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest">Student Profile</th>
              <th className="px-8 py-5 text-[11px] font-black text-slate-400 uppercase tracking-widest text-center">Marking</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-slate-50">
            {filteredStudents.map((student) => (
              <tr key={student.id} className="hover:bg-slate-50/50 transition-all duration-200">
                <td className="px-8 py-6">
                  <span className="text-sm font-bold text-slate-400 bg-slate-100 px-3 py-1.5 rounded-lg">{student.rollNo}</span>
                </td>
                <td className="px-8 py-6">
                  <div className="flex items-center gap-4">
                    <div className="w-11 h-11 rounded-2xl bg-indigo-50 text-indigo-600 flex items-center justify-center font-black text-sm border-2 border-indigo-100/50 shadow-sm">
                      {student.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <div>
                      <p className="font-bold text-slate-900">{student.name}</p>
                      <p className="text-[10px] text-slate-400 font-bold uppercase tracking-wider">{student.department}</p>
                    </div>
                  </div>
                </td>
                <td className="px-8 py-6">
                  <div className="flex justify-center items-center gap-2 sm:gap-3">
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                      className={`px-4 sm:px-6 py-2 sm:py-2.5 rounded-xl text-[10px] sm:text-xs font-black uppercase tracking-widest transition-all ${
                        marking[student.id] === AttendanceStatus.PRESENT 
                          ? 'bg-emerald-500 text-white shadow-lg shadow-emerald-500/30' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      P
                    </button>
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                      className={`px-4 sm:px-6 py-2 sm:py-2.5 rounded-xl text-[10px] sm:text-xs font-black uppercase tracking-widest transition-all ${
                        marking[student.id] === AttendanceStatus.ABSENT 
                          ? 'bg-rose-500 text-white shadow-lg shadow-rose-500/30' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      A
                    </button>
                    <button 
                      onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                      className={`px-4 sm:px-6 py-2 sm:py-2.5 rounded-xl text-[10px] sm:text-xs font-black uppercase tracking-widest transition-all ${
                        marking[student.id] === AttendanceStatus.OD 
                          ? 'bg-sky-500 text-white shadow-lg shadow-sky-500/30' 
                          : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                      }`}
                    >
                      OD
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {lastSummary && (
        <div className="animate-in fade-in slide-in-from-top-10 duration-700 bg-white rounded-[2.5rem] border border-slate-100 shadow-2xl p-6 lg:p-10 mt-12 overflow-hidden relative">
          <div className="flex items-center gap-4 mb-10">
            <div className="w-12 lg:w-14 h-12 lg:h-14 bg-indigo-600 rounded-2xl flex items-center justify-center text-white shadow-xl">
              <UserCheck size={28} />
            </div>
            <div>
              <h2 className="text-2xl lg:text-3xl font-black text-slate-900">Session Summary</h2>
              <p className="text-slate-500 font-medium italic text-sm">Data stored in {selectedDate} folder - {period}</p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 lg:gap-10">
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-emerald-100">
                <h3 className="text-lg font-black text-emerald-600 flex items-center gap-2"><UserCheck size={20} /> Present ({lastSummary.present.length})</h3>
              </div>
              <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-1 gap-2">
                {lastSummary.present.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-semibold text-sm">
                    <div className="w-1.5 h-1.5 rounded-full bg-emerald-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-rose-100">
                <h3 className="text-lg font-black text-rose-600 flex items-center gap-2"><UserX size={20} /> Absent ({lastSummary.absent.length})</h3>
              </div>
              <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-1 gap-2">
                {lastSummary.absent.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-semibold text-sm">
                    <div className="w-1.5 h-1.5 rounded-full bg-rose-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-sky-100">
                <h3 className="text-lg font-black text-sky-600 flex items-center gap-2"><Clock size={20} /> OD ({lastSummary.od.length})</h3>
              </div>
              <ul className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-1 gap-2">
                {lastSummary.od.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-semibold text-sm">
                    <div className="w-1.5 h-1.5 rounded-full bg-sky-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}

      {showSavedToast && (
        <div className="fixed bottom-10 left-1/2 -translate-x-1/2 bg-slate-900 text-white px-8 py-5 rounded-[2rem] shadow-2xl flex items-center gap-5 animate-in fade-in slide-in-from-bottom-10 z-[100]">
          <div className="w-10 h-10 rounded-2xl bg-emerald-500 flex items-center justify-center shadow-lg shadow-emerald-500/20">
            <Check size={22} className="text-white" />
          </div>
          <div>
            <p className="font-black text-sm">Attendance Logged</p>
            <p className="text-[10px] text-slate-300 font-bold uppercase tracking-wider">Session folder created</p>
          </div>
          <button onClick={() => setShowSavedToast(false)} className="ml-6 p-2 text-slate-500 hover:text-white bg-slate-800 rounded-xl">
            <XCircle size={20} />
          </button>
        </div>
      )}
    </div>
  );
};

export default Attendance;

import React, { useState, useMemo } from 'react';
import { 
  Folder, 
  ChevronRight, 
  Calendar, 
  FileText, 
  ArrowLeft,
  UserCheck,
  UserX,
  History as HistoryIcon
} from 'lucide-react';
import { AttendanceRecord, Student } from '../types';

interface HistoryProps {
  records: AttendanceRecord[];
  students: Student[];
}

const HistoryPage: React.FC<HistoryProps> = ({ records, students }) => {
  const [selectedFolder, setSelectedFolder] = useState<string | null>(null);
  const [selectedSession, setSelectedSession] = useState<string | null>(null);

  const dateFolders = useMemo(() => {
    const folders: Record<string, AttendanceRecord[]> = {};
    records.forEach(r => {
      if (!folders[r.date]) folders[r.date] = [];
      folders[r.date].push(r);
    });
    return folders;
  }, [records]);

  const sessionsInFolder = useMemo(() => {
    if (!selectedFolder) return {};
    const sessionMap: Record<string, AttendanceRecord[]> = {};
    dateFolders[selectedFolder].forEach(r => {
      if (!sessionMap[r.timestamp]) sessionMap[r.timestamp] = [];
      sessionMap[r.timestamp].push(r);
    });
    return sessionMap;
  }, [selectedFolder, dateFolders]);

  const currentSessionRecords = useMemo(() => {
    if (!selectedSession || !selectedFolder) return [];
    return sessionsInFolder[selectedSession];
  }, [selectedSession, sessionsInFolder]);

  if (records.length === 0) {
    return (
      <div className="p-10 lg:p-20 text-center flex flex-col items-center gap-6">
        <div className="w-20 lg:w-24 h-20 lg:h-24 bg-slate-100 rounded-full flex items-center justify-center text-slate-300">
          <HistoryIcon size={48} />
        </div>
        <div>
          <h2 className="text-xl lg:text-2xl font-black text-slate-900">No Logs Found</h2>
          <p className="text-slate-500 mt-2 font-medium">Recorded sessions will appear as folders here.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 lg:p-10 max-w-6xl mx-auto space-y-8 animate-in fade-in duration-500">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tight">Campus Logs</h1>
          <p className="text-slate-500 mt-2 font-medium">Browsing automated attendance folders</p>
        </div>
        {selectedFolder && (
          <button 
            onClick={() => { setSelectedFolder(null); setSelectedSession(null); }}
            className="flex items-center justify-center gap-2 px-6 py-3 bg-white border border-slate-200 rounded-2xl text-slate-600 font-bold hover:bg-slate-50 transition-all shadow-sm"
          >
            <ArrowLeft size={18} />
            Folders
          </button>
        )}
      </div>

      {!selectedFolder ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {Object.keys(dateFolders).sort().reverse().map(date => (
            <button 
              key={date}
              onClick={() => setSelectedFolder(date)}
              className="bg-white p-6 rounded-[2rem] border-2 border-slate-50 shadow-sm hover:shadow-xl hover:border-indigo-100 transition-all text-left group"
            >
              <div className="w-14 h-14 bg-indigo-50 text-indigo-500 rounded-2xl flex items-center justify-center mb-6 group-hover:scale-110 transition-transform">
                <Folder size={28} fill="currentColor" fillOpacity={0.1} />
              </div>
              <p className="text-sm font-black text-slate-900 mb-1">{new Date(date).toLocaleDateString(undefined, { month: 'long', day: 'numeric', year: 'numeric' })}</p>
              <p className="text-xs text-slate-400 font-bold uppercase tracking-wider">
                {Array.from(new Set(dateFolders[date].map(r => r.timestamp))).length} Sessions
              </p>
            </button>
          ))}
        </div>
      ) : !selectedSession ? (
        <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden">
