import React, { useState } from 'react';
import { 
  LayoutDashboard, 
  CalendarCheck, 
  Users, 
  LogOut,
  ChevronRight,
  History,
  AlertTriangle
} from 'lucide-react';

interface SidebarProps {
  activeTab: string;
  setActiveTab: (tab: string) => void;
}

const Sidebar: React.FC<SidebarProps> = ({ activeTab, setActiveTab }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);

  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: LayoutDashboard },
    { id: 'attendance', label: 'Attendance', icon: CalendarCheck },
    { id: 'history', label: 'History Logs', icon: History },
    { id: 'students', label: 'Student List', icon: Users },
  ];

  const handleLogout = () => {
    window.location.reload(); 
  };

  return (
    <>
      <div className="w-64 bg-slate-900 h-screen text-white flex flex-col z-50 shadow-2xl border-r border-slate-800">
        <div className="p-8 flex items-center gap-3">
          <div className="w-10 h-10 bg-indigo-500 rounded-xl flex items-center justify-center shadow-lg shadow-indigo-500/30">
            <CalendarCheck size={24} className="text-white" />
          </div>
          <span className="text-xl font-bold tracking-tight">EduManage</span>
        </div>

        <nav className="flex-1 px-4 mt-4">
          <ul className="space-y-1.5">
            {menuItems.map((item) => (
              <li key={item.id}>
                <button
                  onClick={() => setActiveTab(item.id)}
                  className={`w-full flex items-center gap-3 px-4 py-3 rounded-xl transition-all duration-300 group ${
                    activeTab === item.id 
                      ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-600/20 translate-x-1' 
                      : 'text-slate-400 hover:bg-slate-800 hover:text-white hover:translate-x-1'
                  }`}
                >
                  <item.icon size={20} className={activeTab === item.id ? 'text-white' : 'text-slate-400 group-hover:text-white'} />
                  <span className="font-semibold flex-1 text-left">{item.label}</span>
                  {activeTab === item.id && <ChevronRight size={16} />}
                </button>
              </li>
            ))}
