      )}
    </div>
  );
};

export default Attendance;

import React, { useState, useEffect, useRef } from 'react';
import { 
  Search,
  Check,
  XCircle,
  UserCheck,
  UserX,
  Zap,
  ChevronDown,
  ChevronRight as ChevronRightIcon,
  Calendar as CalendarIcon,
  RotateCcw,
  Clock
} from 'lucide-react';
import { Student, AttendanceStatus, AttendanceRecord } from '../types';
import { CLASSES, ELECTIVES } from '../constants';

interface AttendanceProps {
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const Attendance: React.FC<AttendanceProps> = ({ students, onSave }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedClass, setSelectedClass] = useState(CLASSES[1]);
  const [selectedElective, setSelectedElective] = useState(ELECTIVES[0]);
  const [selectedPeriods, setSelectedPeriods] = useState<number[]>([1]);
  const [searchTerm, setSearchTerm] = useState('');
  const [marking, setMarking] = useState<Record<string, AttendanceStatus>>({});
  const [showSavedToast, setShowSavedToast] = useState(false);
  const [lastSummary, setLastSummary] = useState<{
    present: string[],
    absent: string[],
    od: string[]
  } | null>(null);

  const dateInputRef = useRef<HTMLInputElement>(null);
  const availablePeriods = [1, 2, 3, 4, 5, 6, 7];

  useEffect(() => {
    const initial: Record<string, AttendanceStatus> = {};
    students.forEach(s => {
      initial[s.id] = AttendanceStatus.PRESENT;
    });
    setMarking(initial);
  }, [students]);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setMarking(prev => ({ ...prev, [studentId]: status }));
  };

  const handleMarkAll = (status: AttendanceStatus) => {
    const next = { ...marking };
    students.forEach(s => next[s.id] = status);
    setMarking(next);
  };

  const togglePeriod = (p: number) => {
    setSelectedPeriods(prev => 
      prev.includes(p) 
        ? prev.filter(x => x !== p) 
        : [...prev, p].sort((a, b) => a - b)
    );
  };

  const handleNextDay = () => {
    const current = new Date(selectedDate);
    if (isNaN(current.getTime())) {
      setSelectedDate(new Date().toISOString().split('T')[0]);
      return;
    }
    current.setDate(current.getDate() + 1);
    setSelectedDate(current.toISOString().split('T')[0]);
  };

  const handleSetToday = () => {
    setSelectedDate(new Date().toISOString().split('T')[0]);
  };

  const openCalendar = () => {
    if (dateInputRef.current) {
      if ('showPicker' in HTMLInputElement.prototype) {
        try {
          dateInputRef.current.showPicker();
        } catch (e) {
          dateInputRef.current.focus();
          dateInputRef.current.click();
        }
      } else {
        dateInputRef.current.focus();
        dateInputRef.current.click();
      }
    }
  };

  const handleSubmit = () => {
    if (selectedPeriods.length === 0) {
      alert("Please select at least one period.");
      return;
    }

    const now = new Date();
    const timestamp = now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const periodLabel = selectedPeriods.join(',');

    const records: AttendanceRecord[] = students.map(s => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: s.id,
      date: selectedDate,
      timestamp: `Period ${periodLabel} - ${timestamp}`,
      status: marking[s.id] || AttendanceStatus.PRESENT,
      class: selectedClass,
      subject: selectedElective,
    }));

    const summary = {
      present: students.filter(s => marking[s.id] === AttendanceStatus.PRESENT).map(s => s.name),
      absent: students.filter(s => marking[s.id] === AttendanceStatus.ABSENT).map(s => s.name),
      od: students.filter(s => marking[s.id] === AttendanceStatus.OD).map(s => s.name)
    };

    onSave(records);
    setLastSummary(summary);
    setShowSavedToast(true);
    window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' });
    setTimeout(() => setShowSavedToast(false), 3000);
  };

  const filteredStudents = students.filter(s => 
    s.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    s.rollNo.includes(searchTerm)
  );

  return (
    <div className="p-4 lg:p-10 max-w-7xl mx-auto space-y-6 lg:space-y-8 pb-32">
      {/* Header Section */}
      <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tight">Attendance Desk</h1>
          <p className="text-slate-500 mt-1 font-medium italic text-sm">Create session folders for campus records</p>
        </div>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 w-full lg:w-auto">
          <button 
            onClick={() => handleMarkAll(AttendanceStatus.PRESENT)}
            className="px-6 py-4 bg-white border-2 border-slate-100 rounded-2xl text-slate-600 font-bold hover:border-indigo-100 hover:bg-slate-50 transition-all flex items-center justify-center gap-2 active:scale-95 shadow-sm"
          >
            <UserCheck size={18} className="text-emerald-500" />
            Bulk Present
          </button>
          <button 
            onClick={handleSubmit}
            className="px-6 py-4 bg-slate-900 text-white rounded-2xl font-bold hover:bg-slate-800 shadow-xl shadow-slate-200 transition-all flex items-center justify-center gap-3 active:scale-95"
          >
            <Zap size={20} className="fill-indigo-500 text-indigo-500" />
            Commit Record
          </button>
        </div>
      </div>

      {/* Settings Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-5 gap-4">
        {/* FOLDER DATE - Highly Responsive Card */}
        <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm flex flex-col justify-between">
          <div className="flex items-center justify-between mb-3 px-1">
            <label className="text-[10px] font-black text-slate-400 uppercase tracking-widest">Folder Date</label>
            <div className="flex items-center gap-3">
              <button 
                onClick={handleSetToday}
                className="text-[10px] font-black text-slate-400 uppercase flex items-center gap-1 hover:text-indigo-600 transition-colors"
              >
                Today
              </button>
              <button 
                onClick={handleNextDay}
                className="text-[10px] font-black text-indigo-600 uppercase flex items-center gap-1 hover:text-indigo-800 transition-colors"
              >
                Next <ChevronRightIcon size={12} />
              </button>
            </div>
          </div>
          <div className="relative">
            <input 
              ref={dateInputRef}
              type="date" 
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              onClick={(e) => (e.target as any).showPicker?.()}
              className="w-full bg-slate-50 border-2 border-transparent focus:border-indigo-500/20 rounded-xl text-sm font-bold text-slate-700 py-3.5 px-4 outline-none appearance-none cursor-pointer" 
            />
            <div 
              onClick={openCalendar}
              className="absolute right-3 top-1/2 -translate-y-1/2 p-2 text-slate-300 hover:text-indigo-600 transition-colors cursor-pointer"
            >
              <CalendarIcon size={18} />
            </div>
          </div>
        </div>

        {/* PERIOD SELECTION */}
        <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3 px-1">Period</label>
          <div className="flex flex-wrap gap-1.5">
            {availablePeriods.map(p => (
              <button
                key={p}
                onClick={() => togglePeriod(p)}
                className={`w-9 h-9 rounded-xl text-xs font-black transition-all ${
                  selectedPeriods.includes(p) 
                  ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-200' 
                  : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                }`}
              >
                {p}
              </button>
            ))}
          </div>
        </div>

        {/* SUBJECT */}
        <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3 px-1">Subject</label>
          <div className="relative">
            <select 
              value={selectedElective}
              onChange={(e) => setSelectedElective(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-xl text-sm py-3.5 px-4 outline-none font-bold text-slate-700 appearance-none cursor-pointer"
            >
              {ELECTIVES.map(s => <option key={s} value={s}>{s}</option>)}
            </select>
            <ChevronDown size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" />
          </div>
        </div>

        {/* TARGET CLASS */}
        <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3 px-1">Class</label>
          <div className="relative">
            <select 
              value={selectedClass}
              onChange={(e) => setSelectedClass(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-xl text-sm py-3.5 px-4 outline-none font-bold text-slate-700 appearance-none cursor-pointer"
            >
              {CLASSES.map(c => <option key={c} value={c}>{c}</option>)}
            </select>
            <ChevronDown size={14} className="absolute right-4 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" />
          </div>
        </div>

        {/* SEARCH */}
        <div className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm md:col-span-2 lg:col-span-4 xl:col-span-1">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-widest mb-3 px-1">Quick Search</label>
          <div className="relative">
            <Search size={16} className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-300" />
            <input 
              type="text" 
              placeholder="Find student..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-xl text-sm py-3.5 pl-11 pr-4 outline-none font-bold text-slate-700" 
            />
          </div>
        </div>
      </div>

      {/* Attendance Table */}
      <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden flex flex-col">
        <div className="overflow-x-auto custom-scrollbar">
          <table className="w-full text-left min-w-[500px]">
            <thead className="bg-slate-50/50 border-b border-slate-100">
              <tr>
                <th className="px-6 py-5 text-[10px] font-black text-slate-400 uppercase tracking-widest">Student Info</th>
                <th className="px-6 py-5 text-[10px] font-black text-slate-400 uppercase tracking-widest text-center">Status Control</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {filteredStudents.map((student) => (
                <tr key={student.id} className="hover:bg-slate-50/30 transition-all duration-200">
                  <td className="px-6 py-6">
                    <div className="flex items-center gap-4">
                      <div className="hidden sm:flex w-12 h-12 rounded-2xl bg-indigo-50 text-indigo-600 items-center justify-center font-black text-xs border-2 border-indigo-100/30 shadow-sm shrink-0">
                        {student.name.split(' ').map(n => n[0]).join('')}
                      </div>
                      <div className="min-w-0">
                        <p className="font-bold text-slate-900 text-sm truncate">{student.name}</p>
                        <div className="flex items-center gap-2 mt-0.5">
                          <span className="text-[10px] font-black text-slate-400 uppercase tracking-wider">{student.rollNo}</span>
                          <span className="w-1 h-1 rounded-full bg-slate-200"></span>
                          <span className="text-[10px] font-black text-indigo-500 uppercase tracking-wider">{student.department}</span>
                        </div>
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-6">
                    <div className="flex justify-center items-center gap-2">
                      <button 
                        onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                        className={`flex-1 sm:flex-none px-4 sm:px-6 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                          marking[student.id] === AttendanceStatus.PRESENT 
                            ? 'bg-emerald-500 text-white shadow-lg shadow-emerald-500/30' 
                            : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                        }`}
                      >
                        P
                      </button>
                      <button 
                        onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                        className={`flex-1 sm:flex-none px-4 sm:px-6 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                          marking[student.id] === AttendanceStatus.ABSENT 
                            ? 'bg-rose-500 text-white shadow-lg shadow-rose-500/30' 
                            : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                        }`}
                      >
                        A
                      </button>
                      <button 
                        onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                        className={`flex-1 sm:flex-none px-4 sm:px-6 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all ${
                          marking[student.id] === AttendanceStatus.OD 
                            ? 'bg-sky-500 text-white shadow-lg shadow-sky-500/30' 
                            : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                        }`}
                      >
                        OD
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Summary Section */}
      {lastSummary && (
        <div className="animate-in fade-in slide-in-from-top-10 duration-700 bg-white rounded-[2.5rem] border border-slate-100 shadow-xl p-8 lg:p-12 mt-12 overflow-hidden relative">
          <div className="flex items-center gap-5 mb-10">
            <div className="w-14 h-14 bg-indigo-600 rounded-3xl flex items-center justify-center text-white shadow-2xl shadow-indigo-600/20">
              <UserCheck size={28} />
            </div>
            <div>
              <h2 className="text-2xl lg:text-3xl font-black text-slate-900 leading-tight">Session Results</h2>
              <p className="text-slate-500 font-medium italic text-sm mt-0.5">Saved to folder: {selectedDate} â€¢ Period: {selectedPeriods.join(',')}</p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-emerald-50">
                <h3 className="text-sm font-black text-emerald-600 uppercase tracking-widest flex items-center gap-2">Present ({lastSummary.present.length})</h3>
              </div>
              <ul className="space-y-3">
                {lastSummary.present.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-bold text-xs">
                    <div className="w-2 h-2 rounded-full bg-emerald-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-rose-50">
                <h3 className="text-sm font-black text-rose-600 uppercase tracking-widest flex items-center gap-2">Absent ({lastSummary.absent.length})</h3>
              </div>
              <ul className="space-y-3">
                {lastSummary.absent.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-bold text-xs">
                    <div className="w-2 h-2 rounded-full bg-rose-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
            <div className="space-y-6">
              <div className="flex items-center justify-between pb-4 border-b border-sky-50">
                <h3 className="text-sm font-black text-sky-600 uppercase tracking-widest flex items-center gap-2">OD ({lastSummary.od.length})</h3>
              </div>
              <ul className="space-y-3">
                {lastSummary.od.map(name => (
                  <li key={name} className="flex items-center gap-3 text-slate-600 font-bold text-xs">
                    <div className="w-2 h-2 rounded-full bg-sky-400"></div>{name}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}

      {/* Toast Notification */}
      {showSavedToast && (
        <div className="fixed bottom-10 left-4 right-4 sm:left-1/2 sm:right-auto sm:-translate-x-1/2 bg-slate-900 text-white p-5 rounded-3xl shadow-2xl flex items-center gap-4 animate-in fade-in slide-in-from-bottom-10 z-[100] border border-slate-800">
          <div className="w-10 h-10 rounded-2xl bg-emerald-500 flex items-center justify-center shadow-lg shadow-emerald-500/20 shrink-0">
            <Check size={22} className="text-white" />
          </div>
          <div className="flex-1">
            <p className="font-black text-sm">Attendance Logged</p>
            <p className="text-[10px] text-slate-400 font-bold uppercase tracking-widest">Folder synchronization complete</p>
          </div>
          <button onClick={() => setShowSavedToast(false)} className="p-2 text-slate-500 hover:text-white bg-slate-800 rounded-xl">
            <XCircle size={20} />
          </button>
        </div>
      )}
    </div>
  );
};

export default Attendance;

import React, { useState, useEffect, useRef } from 'react';
import { 
  Search,
  Check,
  XCircle,
  UserCheck,
  UserX,
  Zap,
  ChevronDown,
  ChevronRight as ChevronRightIcon,
  Calendar as CalendarIcon,
  RotateCcw
} from 'lucide-react';
import { Student, AttendanceStatus, AttendanceRecord } from '../types';
import { CLASSES, ELECTIVES } from '../constants';

interface AttendanceProps {
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const Attendance: React.FC<AttendanceProps> = ({ students, onSave }) => {
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedClass, setSelectedClass] = useState(CLASSES[1]);
  const [selectedElective, setSelectedElective] = useState(ELECTIVES[0]);
  const [selectedPeriods, setSelectedPeriods] = useState<number[]>([1]);
  const [searchTerm, setSearchTerm] = useState('');
  const [marking, setMarking] = useState<Record<string, AttendanceStatus>>({});
  const [showSavedToast, setShowSavedToast] = useState(false);
  const [lastSummary, setLastSummary] = useState<{
    present: string[],
    absent: string[],
    od: string[]
  } | null>(null);

  const dateInputRef = useRef<HTMLInputElement>(null);
  const availablePeriods = [1, 2, 3, 4, 5, 6, 7];

  useEffect(() => {
    const initial: Record<string, AttendanceStatus> = {};
    students.forEach(s => {
      initial[s.id] = AttendanceStatus.PRESENT;
    });
    setMarking(initial);
  }, [students]);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setMarking(prev => ({ ...prev, [studentId]: status }));
  };

  const handleMarkAll = (status: AttendanceStatus) => {
    const next = { ...marking };
    students.forEach(s => next[s.id] = status);
    setMarking(next);
  };

  const togglePeriod = (p: number) => {
    setSelectedPeriods(prev => 
      prev.includes(p) 
        ? prev.filter(x => x !== p) 
        : [...prev, p].sort((a, b) => a - b)
    );
  };

  const handleNextDay = () => {
    const current = new Date(selectedDate);
    if (isNaN(current.getTime())) {
      setSelectedDate(new Date().toISOString().split('T')[0]);
      return;
    }
    current.setDate(current.getDate() + 1);
    setSelectedDate(current.toISOString().split('T')[0]);
  };

  const handleSetToday = () => {
    setSelectedDate(new Date().toISOString().split('T')[0]);
  };

  const openCalendar = () => {
    dateInputRef.current?.showPicker?.();
  };

  const handleSubmit = () => {
    if (selectedPeriods.length === 0) {
      alert("Please select at least one period.");
      return;
    }

    const now = new Date();
    const timestamp = now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const periodLabel = selectedPeriods.join(',');

    const records: AttendanceRecord[] = students.map(s => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: s.id,
      date: selectedDate,
      timestamp: `Period ${periodLabel} - ${timestamp}`,
      status: marking[s.id] || AttendanceStatus.PRESENT,
      class: selectedClass,
      subject: selectedElective,
    }));

    const summary = {
      present: students.filter(s => marking[s.id] === AttendanceStatus.PRESENT).map(s => s.name),
      absent: students.filter(s => marking[s.id] === AttendanceStatus.ABSENT).map(s => s.name),
      od: students.filter(s => marking[s.id] === AttendanceStatus.OD).map(s => s.name)
    };

    onSave(records);
    setLastSummary(summary);
    setShowSavedToast(true);
    window.scrollTo({ top: document.body.scrollHeight, behavior: 'smooth' });
    setTimeout(() => setShowSavedToast(false), 3000);
  };

  const filteredStudents = students.filter(s => 
    s.name.toLowerCase().includes(searchTerm.toLowerCase()) || 
    s.rollNo.includes(searchTerm)
  );

  return (
    <div className="p-4 lg:p-10 max-w-7xl mx-auto space-y-6 lg:space-y-10 pb-32 overflow-x-hidden">
      {/* Header Section */}
      <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-5xl font-black text-slate-900 tracking-tighter">Attendance Desk</h1>
          <p className="text-slate-500 mt-1 font-medium italic text-sm lg:text-base">Session synchronization & folder management</p>
        </div>
        <div className="flex flex-col sm:flex-row gap-3 w-full lg:w-auto">
          <button 
            onClick={() => handleMarkAll(AttendanceStatus.PRESENT)}
            className="flex-1 lg:flex-none px-6 py-4 bg-white border-2 border-slate-100 rounded-2xl text-slate-600 font-bold hover:border-indigo-100 transition-all flex items-center justify-center gap-2 active:scale-95 shadow-sm"
          >
            <UserCheck size={18} className="text-emerald-500" />
            Bulk Present
          </button>
          <button 
            onClick={handleSubmit}
            className="flex-1 lg:flex-none px-8 py-4 bg-slate-900 text-white rounded-2xl font-black hover:bg-slate-800 shadow-2xl shadow-slate-200 transition-all flex items-center justify-center gap-3 active:scale-95"
          >
            <Zap size={20} className="fill-indigo-500 text-indigo-500" />
            Commit Record
          </button>
        </div>
      </div>

      {/* Control Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 lg:gap-6">
        {/* Date Field */}
        <div className="bg-white p-6 rounded-[2rem] border border-slate-100 shadow-sm flex flex-col justify-between">
          <div className="flex items-center justify-between mb-4">
            <label className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Folder Date</label>
            <div className="flex gap-4">
              <button onClick={handleSetToday} className="text-[10px] font-black text-slate-400 hover:text-indigo-600 uppercase transition-colors flex items-center gap-1">Today</button>
              <button onClick={handleNextDay} className="text-[10px] font-black text-indigo-600 hover:text-indigo-800 uppercase transition-colors flex items-center gap-1">Next <ChevronRightIcon size={12} /></button>
            </div>
          </div>
          <div className="relative" onClick={openCalendar}>
            <input 
              ref={dateInputRef}
              type="date" 
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-2xl text-sm font-black text-slate-700 py-4 px-5 outline-none cursor-pointer appearance-none" 
            />
            <CalendarIcon size={18} className="absolute right-5 top-1/2 -translate-y-1/2 text-slate-300 pointer-events-none" />
          </div>
        </div>

        {/* Period selection */}
        <div className="bg-white p-6 rounded-[2rem] border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-4">Period</label>
          <div className="flex flex-wrap gap-2">
            {availablePeriods.map(p => (
              <button
                key={p}
                onClick={() => togglePeriod(p)}
                className={`w-10 h-10 rounded-xl text-xs font-black transition-all ${
                  selectedPeriods.includes(p) 
                  ? 'bg-indigo-600 text-white shadow-lg shadow-indigo-200' 
                  : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                }`}
              >
                {p}
              </button>
            ))}
          </div>
        </div>

        {/* Subject & Class */}
        <div className="bg-white p-6 rounded-[2rem] border border-slate-100 shadow-sm grid grid-cols-2 gap-4">
          <div className="flex flex-col justify-between">
            <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-4">Subject</label>
            <div className="relative">
              <select 
                value={selectedElective}
                onChange={(e) => setSelectedElective(e.target.value)}
                className="w-full bg-slate-50 border-none rounded-xl text-xs py-4 pl-4 pr-8 outline-none font-black text-slate-700 appearance-none cursor-pointer"
              >
                {ELECTIVES.map(s => <option key={s} value={s}>{s}</option>)}
              </select>
              <ChevronDown size={14} className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 pointer-events-none" />
            </div>
          </div>
          <div className="flex flex-col justify-between">
            <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-4">Class</label>
            <div className="relative">
              <select 
                value={selectedClass}
                onChange={(e) => setSelectedClass(e.target.value)}
                className="w-full bg-slate-50 border-none rounded-xl text-xs py-4 pl-4 pr-8 outline-none font-black text-slate-700 appearance-none cursor-pointer"
              >
                {CLASSES.map(c => <option key={c} value={c}>{c}</option>)}
              </select>
              <ChevronDown size={14} className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-300 pointer-events-none" />
            </div>
          </div>
        </div>

        {/* Search */}
        <div className="bg-white p-6 rounded-[2rem] border border-slate-100 shadow-sm">
          <label className="block text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-4">Search Register</label>
          <div className="relative">
            <Search size={18} className="absolute left-5 top-1/2 -translate-y-1/2 text-slate-300" />
            <input 
              type="text" 
              placeholder="Find student..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full bg-slate-50 border-none rounded-2xl text-sm py-4 pl-12 pr-4 outline-none font-bold text-slate-700" 
            />
          </div>
        </div>
      </div>

      {/* Student List - Card style for mobile, Table for desktop */}
      <div className="space-y-4">
        <div className="hidden lg:block bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden">
          <table className="w-full text-left">
            <thead className="bg-slate-50/50 border-b border-slate-100">
              <tr>
                <th className="px-10 py-6 text-[11px] font-black text-slate-400 uppercase tracking-widest">Student Profile</th>
                <th className="px-10 py-6 text-[11px] font-black text-slate-400 uppercase tracking-widest text-center">Mark Status</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {filteredStudents.map((student) => (
                <tr key={student.id} className="hover:bg-slate-50/30 transition-all">
                  <td className="px-10 py-6">
                    <div className="flex items-center gap-4">
                      <div className="w-12 h-12 rounded-2xl bg-indigo-50 text-indigo-600 flex items-center justify-center font-black text-xs border-2 border-indigo-100/30">
                        {student.name.split(' ').map(n => n[0]).join('')}
                      </div>
                      <div>
                        <p className="font-black text-slate-900">{student.name}</p>
                        <p className="text-[10px] font-black text-indigo-500/60 uppercase tracking-wider">{student.rollNo} â€¢ {student.department}</p>
                      </div>
                    </div>
                  </td>
                  <td className="px-10 py-6">
                    <div className="flex justify-center items-center gap-3">
                      {[
                        { status: AttendanceStatus.PRESENT, label: 'P', color: 'emerald' },
                        { status: AttendanceStatus.ABSENT, label: 'A', color: 'rose' },
                        { status: AttendanceStatus.OD, label: 'OD', color: 'sky' }
                      ].map(opt => (
                        <button 
                          key={opt.status}
                          onClick={() => handleStatusChange(student.id, opt.status)}
                          className={`w-14 h-12 rounded-2xl text-xs font-black uppercase tracking-widest transition-all ${
                            marking[student.id] === opt.status 
                              ? `bg-${opt.color}-500 text-white shadow-xl shadow-${opt.color}-500/30` 
                              : 'bg-slate-50 text-slate-400 hover:bg-slate-100'
                          }`}
                        >
                          {opt.label}
                        </button>
                      ))}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Mobile View Cards */}
        <div className="lg:hidden grid grid-cols-1 gap-4">
          {filteredStudents.map((student) => (
            <div key={student.id} className="bg-white p-5 rounded-[2rem] border border-slate-100 shadow-sm space-y-4">
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded-2xl bg-indigo-50 text-indigo-600 flex items-center justify-center font-black text-xs border-2 border-indigo-100/30">
                  {student.name.split(' ').map(n => n[0]).join('')}
                </div>
                <div className="min-w-0">
                  <p className="font-black text-slate-900 text-base truncate">{student.name}</p>
                  <p className="text-[10px] font-black text-slate-400 uppercase tracking-wider truncate">{student.rollNo} â€¢ {student.department}</p>
                </div>
              </div>
              <div className="grid grid-cols-3 gap-2">
                {[
                  { status: AttendanceStatus.PRESENT, label: 'Present', color: 'emerald' },
                  { status: AttendanceStatus.ABSENT, label: 'Absent', color: 'rose' },
                  { status: AttendanceStatus.OD, label: 'OD', color: 'sky' }
                ].map(opt => (
                  <button 
                    key={opt.status}
                    onClick={() => handleStatusChange(student.id, opt.status)}
                    className={`py-3 rounded-2xl text-[10px] font-black uppercase tracking-widest transition-all ${
                      marking[student.id] === opt.status 
                        ? `bg-${opt.color}-500 text-white shadow-lg` 
                        : 'bg-slate-50 text-slate-400'
                    }`}
                  >
                    {opt.label}
                  </button>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Summary View */}
      {lastSummary && (
        <div className="animate-in fade-in slide-in-from-top-10 duration-700 bg-white rounded-[3rem] border border-slate-100 shadow-2xl p-8 lg:p-14 mt-12 overflow-hidden relative">
          <div className="flex flex-col md:flex-row md:items-center gap-6 mb-12">
            <div className="w-16 h-16 bg-indigo-600 rounded-[1.5rem] flex items-center justify-center text-white shadow-2xl shadow-indigo-600/20">
              <RotateCcw size={32} />
            </div>
            <div>
              <h2 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tighter leading-tight">Sync Report</h2>
              <p className="text-slate-500 font-bold italic text-sm lg:text-base">Target: {selectedDate} â€¢ Period {selectedPeriods.join(', ')}</p>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
            {[
              { label: 'Present', data: lastSummary.present, color: 'emerald', icon: UserCheck },
              { label: 'Absent', data: lastSummary.absent, color: 'rose', icon: UserX },
              { label: 'On Duty', data: lastSummary.od, color: 'sky', icon: Clock }
            ].map(col => (
              <div key={col.label} className="space-y-6">
                <div className={`flex items-center justify-between pb-4 border-b border-${col.color}-100`}>
                  <h3 className={`text-sm lg:text-base font-black text-${col.color}-600 uppercase tracking-widest flex items-center gap-3`}>
                    <col.icon size={20} /> {col.label} ({col.data.length})
                  </h3>
                </div>
                <ul className="space-y-3">
                  {col.data.map(name => (
                    <li key={name} className="flex items-center gap-3 text-slate-600 font-bold text-xs lg:text-sm">
                      <div className={`w-1.5 h-1.5 rounded-full bg-${col.color}-400`}></div>{name}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Persistent Toast */}
      {showSavedToast && (
        <div className="fixed bottom-10 left-4 right-4 sm:left-1/2 sm:right-auto sm:-translate-x-1/2 bg-slate-900 text-white p-6 rounded-[2rem] shadow-2xl flex items-center gap-5 animate-in fade-in slide-in-from-bottom-10 z-[100] border border-slate-800">
          <div className="w-12 h-12 rounded-2xl bg-emerald-500 flex items-center justify-center shadow-2xl shadow-emerald-500/40 shrink-0">
            <Check size={28} className="text-white" />
          </div>
          <div className="flex-1">
            <p className="font-black text-sm lg:text-base">Sync Complete</p>
            <p className="text-[10px] text-slate-400 font-black uppercase tracking-[0.2em] mt-0.5">Cloud folder updated successfully</p>
          </div>
          <button onClick={() => setShowSavedToast(false)} className="p-2 text-slate-500 hover:text-white transition-colors">
            <XCircle size={24} />
          </button>
        </div>
      )}
    </div>
  );
};

export default Attendance;

import React, { useMemo } from 'react';
import { 
  Users, 
  UserMinus, 
  TrendingUp, 
  CalendarCheck, 
  Clock,
  LayoutGrid
} from 'lucide-react';
import { 
  CartesianGrid, 
  Tooltip, 
  ResponsiveContainer, 
  AreaChart, 
  Area,
  XAxis,
  YAxis
} from 'recharts';
import { Student, AttendanceRecord } from '../types';

interface DashboardProps {
  students: Student[];
  attendance: AttendanceRecord[];
  onTakeAttendance: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ students, attendance, onTakeAttendance }) => {
  const stats = useMemo(() => {
    const today = new Date().toISOString().split('T')[0];
    const todaysAttendance = attendance.filter(a => a.date === today);
    const presentToday = todaysAttendance.filter(a => a.status === 'Present').length;
    const odToday = todaysAttendance.filter(a => a.status === 'OD').length;

    return {
      totalStudents: students.length,
      attendancePercentage: todaysAttendance.length > 0 
        ? Math.round((presentToday / todaysAttendance.length) * 100) 
        : 88,
      absentToday: todaysAttendance.filter(a => a.status === 'Absent').length,
      odToday: odToday,
      departmentsCount: Array.from(new Set(students.map(s => s.department))).length
    };
  }, [students, attendance]);

  const chartData = [
    { name: 'Mon', present: 88 },
    { name: 'Tue', present: 92 },
    { name: 'Wed', present: 85 },
    { name: 'Thu', present: 78 },
    { name: 'Fri', present: 82 },
    { name: 'Sat', present: 90 },
  ];

  const StatCard = ({ title, value, icon: Icon, color, trend }: any) => (
    <div className="bg-white p-5 lg:p-6 rounded-[2rem] border border-slate-100 shadow-sm hover:shadow-xl transition-all duration-300 group">
      <div className="flex justify-between items-start mb-4">
        <div className={`p-3 lg:p-4 rounded-2xl bg-${color}-50 text-${color}-600 group-hover:scale-110 transition-transform`}>
          <Icon size={20} className="lg:w-6 lg:h-6" />
        </div>
        {trend && (
          <div className="flex items-center gap-1 text-emerald-600 text-[10px] lg:text-xs font-black bg-emerald-50 px-2 py-1 rounded-full">
            <TrendingUp size={12} />
            {trend}
          </div>
        )}
      </div>
      <h3 className="text-slate-400 text-[10px] font-black uppercase tracking-widest leading-none">{title}</h3>
      <p className="text-2xl lg:text-3xl font-black text-slate-900 mt-2">{value}</p>
    </div>
  );

  return (
    <div className="p-4 lg:p-10 space-y-6 lg:space-y-10 animate-in fade-in slide-in-from-bottom-6 duration-700 overflow-x-hidden">
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-5xl font-black text-slate-900 tracking-tighter">Campus Overview</h1>
          <p className="text-slate-500 mt-1 font-medium italic text-sm lg:text-base">Real-time attendance & session folder insights</p>
        </div>
        <button 
          onClick={onTakeAttendance}
          className="w-full md:w-auto flex items-center justify-center gap-3 px-8 py-4 bg-indigo-600 text-white rounded-2xl font-black hover:bg-indigo-700 shadow-2xl shadow-indigo-600/30 transition-all active:scale-95"
        >
          <CalendarCheck size={22} />
          New Attendance
        </button>
      </div>

      <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
        <StatCard title="Total Enrolled" value={stats.totalStudents} icon={Users} color="indigo" trend="+4" />
        <StatCard title="Attendance" value={`${stats.attendancePercentage}%`} icon={TrendingUp} color="emerald" trend="+1.2%" />
        <StatCard title="Absent" value={stats.absentToday} icon={UserMinus} color="rose" />
        <StatCard title="On Duty" value={stats.odToday} icon={Clock} color="sky" />
        <div className="col-span-2 lg:col-span-1">
          <StatCard title="Depts" value={stats.departmentsCount} icon={LayoutGrid} color="amber" />
        </div>
      </div>

      <div className="bg-white p-6 lg:p-10 rounded-[3rem] border border-slate-100 shadow-sm">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 mb-10">
          <div>
            <h2 className="text-xl lg:text-2xl font-black text-slate-900 tracking-tight">Performance Analytics</h2>
            <p className="text-sm font-bold text-slate-400 mt-1 uppercase tracking-widest">Weekly campus trend report</p>
          </div>
          <select className="w-full sm:w-auto bg-slate-50 border-none rounded-xl text-xs font-black p-4 outline-none cursor-pointer ring-1 ring-slate-100 appearance-none pr-10">
            <option>This Week</option>
            <option>Last Week</option>
          </select>
        </div>
        <div className="h-[250px] lg:h-[400px]">
          <ResponsiveContainer width="100%" height="100%">
            <AreaChart data={chartData}>
              <defs>
                <linearGradient id="colorInd" x1="0" y1="0" x2="0" y2="1">
                  <stop offset="5%" stopColor="#4f46e5" stopOpacity={0.15}/>
                  <stop offset="95%" stopColor="#4f46e5" stopOpacity={0}/>
                </linearGradient>
              </defs>
              <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f1f5f9" />
              <XAxis 
                dataKey="name" 
                axisLine={false} 
                tickLine={false} 
                tick={{fill: '#94a3b8', fontSize: 10, fontWeight: 900}} 
                dy={15} 
              />
              <YAxis 
                axisLine={false} 
                tickLine={false} 
                tick={{fill: '#94a3b8', fontSize: 10, fontWeight: 900}} 
              />
              <Tooltip 
                contentStyle={{ 
                  borderRadius: '24px', 
                  border: 'none', 
                  boxShadow: '0 25px 50px -12px rgb(0 0 0 / 0.15)', 
                  padding: '16px',
                  backgroundColor: '#0f172a',
                  color: '#fff',
                  fontWeight: 900,
                  fontSize: '12px'
                }}
              />
              <Area type="monotone" dataKey="present" stroke="#4f46e5" strokeWidth={5} fillOpacity={1} fill="url(#colorInd)" />
            </AreaChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

