import React, { useState, useMemo } from 'react';
import Sidebar from './components/Sidebar';
import Dashboard from './pages/Dashboard';
import Attendance from './pages/Attendance';
import Students from './pages/Students';
import HistoryPage from './pages/History';
import AIAssistant from './pages/AIAssistant';
import { MOCK_STUDENTS } from './constants';
import { Student, AttendanceRecord } from './types';
import { Menu, AlertTriangle, Search } from 'lucide-react';

const App: React.FC = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [students, setStudents] = useState<Student[]>(MOCK_STUDENTS);
  const [attendanceRecords, setAttendanceRecords] = useState<AttendanceRecord[]>([]);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [globalSearch, setGlobalSearch] = useState('');

  const handleSaveAttendance = (newRecords: AttendanceRecord[]) => {
    setAttendanceRecords(prev => [...prev, ...newRecords]);
  };

  const handleAddStudent = (student: Omit<Student, 'id' | 'attendancePercentage'>) => {
    const freshStudent: Student = {
      ...student,
      id: Math.random().toString(36).substr(2, 9),
      attendancePercentage: 0
    };
    setStudents(prev => [freshStudent, ...prev]);
  };

  const handleDeleteStudent = (id: string) => {
    setStudents(prev => prev.filter(s => s.id !== id));
  };

  const handleLogout = () => {
    // Reset app state for mock logout
    setAttendanceRecords([]);
    setActiveTab('dashboard');
    setShowLogoutConfirm(false);
    window.location.reload(); 
  };

  const filteredStudents = useMemo(() => {
    if (!globalSearch) return students;
    return students.filter(s => s.name.toLowerCase().includes(globalSearch.toLowerCase()) || s.rollNo.includes(globalSearch));
  }, [students, globalSearch]);

  const renderContent = () => {
    switch (activeTab) {
      case 'dashboard':
        return <Dashboard 
          students={filteredStudents} 
          attendance={attendanceRecords} 
          onTakeAttendance={() => setActiveTab('attendance')} 
        />;
      case 'attendance':
        return <Attendance students={filteredStudents} onSave={handleSaveAttendance} />;
      case 'history':
        return <HistoryPage records={attendanceRecords} students={students} />;
      case 'students':
        return <Students 
          students={filteredStudents} 
          onAdd={handleAddStudent} 
          onDelete={handleDeleteStudent} 
        />;
      case 'ai':
        return <AIAssistant students={students} attendance={attendanceRecords} />;
      default:
        return <Dashboard students={students} attendance={attendanceRecords} onTakeAttendance={() => setActiveTab('attendance')} />;
    }
  };

  return (
    <div className="min-h-screen bg-slate-50 flex overflow-hidden">
      {/* Overlay for mobile sidebar */}
      {isSidebarOpen && (
        <div 
          className="fixed inset-0 bg-slate-950/40 z-40 lg:hidden backdrop-blur-sm transition-opacity"
          onClick={() => setIsSidebarOpen(false)}
        />
      )}

      {/* Sidebar Container */}
      <div className={`fixed inset-y-0 left-0 z-50 transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} lg:translate-x-0 transition-transform duration-300 ease-in-out`}>
        <Sidebar 
          activeTab={activeTab} 
          setActiveTab={(tab) => {
            setActiveTab(tab);
            setIsSidebarOpen(false);
          }}
          onLogoutRequest={() => setShowLogoutConfirm(true)}
        />
      </div>

      <main className="flex-1 lg:ml-64 h-screen flex flex-col relative overflow-hidden">
        <header className="sticky top-0 z-30 bg-white/70 backdrop-blur-xl border-b border-slate-200 px-4 lg:px-10 py-4 flex items-center justify-between">
          <div className="flex items-center gap-4">
            <button 
              onClick={() => setIsSidebarOpen(true)}
              className="lg:hidden p-3 text-slate-500 hover:bg-slate-100 rounded-2xl transition-colors active:scale-90"
            >
              <Menu size={24} />
            </button>
            <div className="relative hidden md:block w-64 lg:w-96">
              <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none text-slate-400">
                <Search size={18} strokeWidth={2.5} />
              </div>
              <input 
                type="text" 
                value={globalSearch}
                onChange={(e) => setGlobalSearch(e.target.value)}
                placeholder="Global campus search..." 
                className="block w-full pl-12 pr-4 py-3 border-none bg-slate-100/50 rounded-2xl text-sm font-bold placeholder-slate-400 focus:outline-none focus:ring-4 focus:ring-indigo-500/10 focus:bg-white transition-all"
              />
            </div>
          </div>

          <div className="flex items-center gap-3 lg:gap-6">
            <div className="flex items-center gap-3 bg-white py-1.5 pl-1.5 pr-3 rounded-2xl border border-slate-100 shadow-sm">
              <div className="w-10 h-10 rounded-full bg-slate-900 flex items-center justify-center text-white font-black text-xs shadow-lg">
                CA
              </div>
              <div className="hidden sm:block">
                <p className="text-[10px] font-black text-slate-900 uppercase tracking-tight">Campus Admin</p>
                <p className="text-[8px] text-indigo-600 font-black tracking-widest uppercase">Academix Pro</p>
              </div>
            </div>
          </div>
        </header>

        <div className="flex-1 overflow-y-auto custom-scrollbar">
          <div className="animate-in fade-in slide-in-from-top-4 duration-500">
            {renderContent()}
          </div>
        </div>
      </main>

      {/* Global Modal - Perfectly Centered */}
      {showLogoutConfirm && (
        <div className="fixed inset-0 bg-slate-950/60 backdrop-blur-md z-[9999] flex items-center justify-center p-4 animate-in fade-in duration-300">
          <div className="bg-white w-full max-w-[360px] rounded-[3rem] shadow-2xl overflow-hidden animate-in zoom-in-95 duration-300 flex flex-col items-center text-center p-10">
            <div className="w-20 h-20 bg-rose-50 text-rose-500 rounded-[2rem] flex items-center justify-center mb-8 ring-[12px] ring-rose-500/5">
              <AlertTriangle size={36} strokeWidth={2.5} />
            </div>

            <h3 className="text-2xl font-black text-slate-900 mb-3 tracking-tighter leading-none">Confirm Exit?</h3>
            <p className="text-slate-500 text-sm font-semibold leading-relaxed px-4 mb-10">
              This will clear your local cached session data and return to the login screen.
            </p>

            <div className="w-full flex items-center gap-4">
              <button 
                onClick={() => setShowLogoutConfirm(false)}
                className="flex-1 py-4 bg-slate-100 text-slate-600 font-black rounded-2xl hover:bg-slate-200 transition-all active:scale-95"
              >
                Go Back
              </button>
              <button 
                onClick={handleLogout}
                className="flex-1 py-4 bg-rose-600 text-white font-black rounded-2xl hover:bg-rose-700 shadow-xl shadow-rose-600/20 transition-all active:scale-95"
              >
                Log Out
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;

