  ArrowLeft,
  UserCheck,
  UserX,
  History as HistoryIcon,
  Trash2,
  Edit,
  AlertCircle,
  MoreVertical
} from 'lucide-react';
import { AttendanceRecord, Student } from '../types';

interface HistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onDeleteFolder: (date: string) => void;
  onDeleteSession: (date: string, timestamp: string) => void;
  onEditSession: (date: string, timestamp: string) => void;
}

const HistoryPage: React.FC<HistoryProps> = ({ 
  records, 
  students, 
  onDeleteFolder, 
  onDeleteSession, 
  onEditSession 
}) => {
  const [selectedFolder, setSelectedFolder] = useState<string | null>(null);
  const [selectedSession, setSelectedSession] = useState<string | null>(null);
  const [confirmDelete, setConfirmDelete] = useState<{ type: 'folder' | 'session', date: string, timestamp?: string } | null>(null);

  const dateFolders = useMemo(() => {
    const folders: Record<string, AttendanceRecord[]> = {};
    records.forEach(r => {
      if (!folders[r.date]) folders[r.date] = [];
      folders[r.date].push(r);
    });
    return folders;
  }, [records]);

  const sessionsInFolder = useMemo(() => {
    if (!selectedFolder) return {};
    const sessionMap: Record<string, AttendanceRecord[]> = {};
    const recordsInDate = dateFolders[selectedFolder] || [];
    recordsInDate.forEach(r => {
      if (!sessionMap[r.timestamp]) sessionMap[r.timestamp] = [];
      sessionMap[r.timestamp].push(r);
    });
    return sessionMap;
  }, [selectedFolder, dateFolders]);

  const currentSessionRecords = useMemo(() => {
    if (!selectedSession || !selectedFolder) return [];
    return sessionsInFolder[selectedSession] || [];
  }, [selectedSession, sessionsInFolder]);

  const handleDeleteConfirm = () => {
    if (!confirmDelete) return;
    if (confirmDelete.type === 'folder') {
      onDeleteFolder(confirmDelete.date);
      if (selectedFolder === confirmDelete.date) setSelectedFolder(null);
    } else if (confirmDelete.type === 'session' && confirmDelete.timestamp) {
      onDeleteSession(confirmDelete.date, confirmDelete.timestamp);
      if (selectedSession === confirmDelete.timestamp) setSelectedSession(null);
    }
    setConfirmDelete(null);
