          <p className="text-slate-500 mt-2 font-medium">Recorded sessions will appear as folders here.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="p-4 lg:p-10 max-w-6xl mx-auto space-y-8 animate-in fade-in duration-500 pb-32">
      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl lg:text-4xl font-black text-slate-900 tracking-tight">Campus Logs</h1>
          <p className="text-slate-500 mt-2 font-medium">Browsing automated attendance folders</p>
        </div>
        {selectedFolder && (
          <button 
            onClick={() => { setSelectedFolder(null); setSelectedSession(null); }}
            className="flex items-center justify-center gap-2 px-6 py-3 bg-white border border-slate-200 rounded-2xl text-slate-600 font-bold hover:bg-slate-50 transition-all shadow-sm"
          >
            <ArrowLeft size={18} />
