                      </span>
                      <ChevronRight size={20} className="text-slate-300 group-hover:translate-x-1 transition-transform" />
                    </div>
                  </div>
                </button>

                {/* Session Action Buttons */}
                <div className="pr-6 flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                  <button 
                    onClick={(e) => { e.stopPropagation(); onEditSession(selectedFolder, sessionTime); }}
                    className="p-3 text-indigo-500 hover:bg-indigo-100 rounded-xl transition-colors"
                    title="Edit Records"
                  >
                    <Edit size={18} />
                  </button>
                  <button 
                    onClick={(e) => { e.stopPropagation(); setConfirmDelete({ type: 'session', date: selectedFolder, timestamp: sessionTime }); }}
                    className="p-3 text-rose-500 hover:bg-rose-100 rounded-xl transition-colors"
                    title="Delete Session"
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      ) : (
        <div className="space-y-6 lg:space-y-8 animate-in slide-in-from-right-10 duration-500">
          <div className="bg-white p-6 lg:p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col md:flex-row justify-between items-start md:items-center gap-6">
            <div className="flex items-center gap-4 lg:gap-6">
              <button 
                onClick={() => setSelectedSession(null)}
