              <div className="px-4 py-2 bg-emerald-50 text-emerald-600 rounded-xl flex items-center gap-2 border border-emerald-100">
                <UserCheck size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Present').length}</span>
              </div>
              <div className="px-4 py-2 bg-rose-50 text-rose-600 rounded-xl flex items-center gap-2 border border-rose-100">
                <UserX size={16} />
                <span className="text-sm font-black">{currentSessionRecords.filter(r => r.status === 'Absent').length}</span>
              </div>
