            <div className="flex gap-4">
              <button onClick={() => setConfirmDelete(null)} className="flex-1 py-4 bg-slate-100 text-slate-600 font-black rounded-2xl">Cancel</button>
              <button onClick={handleDeleteConfirm} className="flex-1 py-4 bg-rose-600 text-white font-black rounded-2xl shadow-xl shadow-rose-600/20">Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

