import { GoogleGenAI, Type } from "@google/genai";
import { AttendanceRecord, Student } from "../types";

export const generateAttendanceReport = async (
  records: AttendanceRecord[],
  students: Student[],
  subject: string,
  date: string
): Promise<string> => {
  const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

  const studentMap = new Map(students.map(s => [s.id, s.name]));
  const summaryData = records.map(r => ({
    student: studentMap.get(r.studentId),
    status: r.status,
    period: r.period
  }));

  const prompt = `Analyze the following attendance data for Subject: ${subject} on Date: ${date}. 
  Data: ${JSON.stringify(summaryData)}

  Please provide a professional summary including:
  1. Attendance Percentage for the day.
  2. Students consistently absent across multiple periods (if any).
  3. Any notable patterns or trends.
  4. Suggestions for the faculty.

  Keep the response concise and formatted in Markdown.`;

  try {
    const response = await ai.models.generateContent({
      model: 'gemini-3-flash-preview',
      contents: prompt,
    });
    return response.text || "No insights could be generated at this time.";
  } catch (error) {
    console.error("Gemini Error:", error);
    return "Error generating AI report. Please check your API configuration.";
  }
};

