import React, { useState } from 'react';
import { User, Student, AttendanceRecord, AttendanceStatus, Subject } from '../types';
import { SUBJECTS } from '../constants';

interface MarkAttendanceProps {
  user: User;
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const MarkAttendance: React.FC<MarkAttendanceProps> = ({ user, students, onSave }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0].name);
  const [selectedPeriod, setSelectedPeriod] = useState(1);
  const [attendance, setAttendance] = useState<Record<string, AttendanceStatus>>({});
  const [isSaved, setIsSaved] = useState(false);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setAttendance(prev => ({ ...prev, [studentId]: status }));
  };

  const handleSave = () => {
    const today = new Date().toISOString().split('T')[0];
    const newRecords: AttendanceRecord[] = students.map(student => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: student.id,
      status: attendance[student.id] || AttendanceStatus.PRESENT,
      date: today,
      period: selectedPeriod,
      subject: selectedSubject,
      facultyId: user.id
    }));

    onSave(newRecords);
    setIsSaved(true);
    setTimeout(() => setIsSaved(false), 3000);
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Configuration Bar */}
      <div className="bg-white rounded-xl shadow-sm p-6 flex flex-wrap gap-4 items-end">
        <div className="flex-1 min-w-[200px]">
          <label className="block text-sm font-semibold text-gray-600 mb-2">Select Subject</label>
          <select 
            className="w-full border rounded-lg p-2 outline-none focus:ring-2 focus:ring-indigo-500"
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            {SUBJECTS.map(s => <option key={s.id} value={s.name}>{s.name} ({s.code})</option>)}
          </select>
        </div>
        <div className="w-32">
          <label className="block text-sm font-semibold text-gray-600 mb-2">Period</label>
          <select 
            className="w-full border rounded-lg p-2 outline-none focus:ring-2 focus:ring-indigo-500"
            value={selectedPeriod}
            onChange={(e) => setSelectedPeriod(Number(e.target.value))}
          >
            {[1, 2, 3, 4, 5, 6, 7].map(p => <option key={p} value={p}>Period {p}</option>)}
          </select>
        </div>
        <button 
          onClick={() => {
            const allPresent: Record<string, AttendanceStatus> = {};
            students.forEach(s => allPresent[s.id] = AttendanceStatus.PRESENT);
            setAttendance(allPresent);
          }}
          className="px-4 py-2 text-sm text-indigo-600 font-medium hover:bg-indigo-50 rounded-lg border border-indigo-200 transition-colors"
        >
          Mark All Present
        </button>
      </div>

      {/* Student List */}
      <div className="bg-white rounded-xl shadow-sm overflow-hidden border">
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead className="bg-gray-50 text-xs font-semibold text-gray-500 uppercase border-b">
              <tr>
                <th className="px-6 py-4 w-24">Roll No</th>
                <th className="px-6 py-4">Student Name</th>
                <th className="px-6 py-4 text-center">Attendance Status</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-100">
              {students.map((student) => {
                const status = attendance[student.id] || AttendanceStatus.PRESENT;
                return (
                  <tr key={student.id} className="hover:bg-gray-50 transition-colors">
                    <td className="px-6 py-4 font-mono text-sm text-gray-500">{student.rollNumber}</td>
                    <td className="px-6 py-4 font-medium text-gray-800">{student.name}</td>
                    <td className="px-6 py-4">
                      <div className="flex justify-center space-x-2">
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                          className={`flex-1 py-1 px-3 rounded-lg text-xs font-bold border transition-all ${
                            status === AttendanceStatus.PRESENT 
                            ? 'bg-green-600 border-green-600 text-white shadow-sm' 
                            : 'bg-white border-gray-200 text-gray-400 hover:border-green-200 hover:text-green-500'
                          }`}
                        >
                          P
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                          className={`flex-1 py-1 px-3 rounded-lg text-xs font-bold border transition-all ${
                            status === AttendanceStatus.ABSENT 
                            ? 'bg-red-600 border-red-600 text-white shadow-sm' 
                            : 'bg-white border-gray-200 text-gray-400 hover:border-red-200 hover:text-red-500'
                          }`}
                        >
                          A
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                          className={`flex-1 py-1 px-3 rounded-lg text-xs font-bold border transition-all ${
                            status === AttendanceStatus.OD 
                            ? 'bg-blue-600 border-blue-600 text-white shadow-sm' 
                            : 'bg-white border-gray-200 text-gray-400 hover:border-blue-200 hover:text-blue-500'
                          }`}
                        >
                          OD
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      <div className="flex justify-end pt-4 sticky bottom-0 bg-gray-50 py-4">
        {isSaved && (
          <div className="mr-4 flex items-center text-green-600 font-medium animate-bounce">
            ‚úÖ Attendance saved successfully!
          </div>
        )}
        <button
          onClick={handleSave}
          className="px-10 py-3 bg-indigo-600 hover:bg-indigo-700 text-white font-bold rounded-xl shadow-lg transition-all active:scale-95"
        >
          Submit Attendance
        </button>
      </div>
    </div>
  );
};

