import React, { useState } from 'react';
import { AttendanceRecord, Student, AttendanceStatus, User, UserRole } from '../types';
import { SUBJECTS } from '../constants';
import { generateAttendanceReport } from '../services/geminiService';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  user: User;
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, user, onUpdate, onDelete }) => {
  const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]);
  const [filterSubject, setFilterSubject] = useState('All');
  const [aiInsight, setAiInsight] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);

  const filteredRecords = records.filter(r => {
    const matchesDate = !filterDate || r.date === filterDate;
    const matchesSubject = filterSubject === 'All' || r.subject === filterSubject;
    return matchesDate && matchesSubject;
  });

  const handleGenerateAI = async () => {
    if (filteredRecords.length === 0) {
      alert("No records to analyze for this criteria.");
      return;
    }
    setIsGenerating(true);
    setAiInsight(null);
    try {
      const insight = await generateAttendanceReport(
        filteredRecords,
        students,
        filterSubject,
        filterDate
      );
      setAiInsight(insight);
    } catch (e) {
      setAiInsight("Error generating report.");
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="bg-white p-6 rounded-xl shadow-sm flex flex-wrap gap-4 items-end">
        <div className="w-full sm:w-auto flex-1 min-w-[150px]">
          <label className="block text-xs font-bold text-gray-500 uppercase mb-2">Date</label>
          <input 
            type="date"
            className="w-full border rounded-lg p-2 outline-none"
            value={filterDate}
            onChange={(e) => setFilterDate(e.target.value)}
          />
        </div>
        <div className="w-full sm:w-auto flex-1 min-w-[150px]">
          <label className="block text-xs font-bold text-gray-500 uppercase mb-2">Subject</label>
          <select 
            className="w-full border rounded-lg p-2 outline-none"
            value={filterSubject}
            onChange={(e) => setFilterSubject(e.target.value)}
          >
            <option value="All">All Subjects</option>
            {SUBJECTS.map(s => <option key={s.id} value={s.name}>{s.name}</option>)}
          </select>
        </div>
        <button 
          onClick={handleGenerateAI}
          disabled={isGenerating}
          className="w-full sm:w-auto px-6 py-2 bg-purple-600 hover:bg-purple-700 text-white rounded-lg font-semibold transition-colors flex items-center justify-center space-x-2 disabled:opacity-50"
        >
          <span>‚ú®</span>
          <span>{isGenerating ? 'Analyzing...' : 'AI Insights'}</span>
        </button>
      </div>

      {aiInsight && (
        <div className="bg-gradient-to-r from-purple-50 to-indigo-50 border border-purple-200 rounded-xl p-6 relative overflow-hidden">
          <button 
            onClick={() => setAiInsight(null)}
            className="absolute top-4 right-4 text-purple-400 hover:text-purple-600"
          >
            ‚úï
          </button>
          <h4 className="text-purple-800 font-bold mb-4 flex items-center">
            <span className="mr-2">üí°</span> Smart Analysis Report
          </h4>
          <div className="prose prose-sm max-w-none text-gray-700 whitespace-pre-line">
            {aiInsight}
          </div>
        </div>
      )}

      <div className="bg-white rounded-xl shadow-sm overflow-hidden border">
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead className="bg-gray-50 text-xs font-semibold text-gray-500 uppercase border-b">
              <tr>
                <th className="px-6 py-4">Student</th>
                <th className="px-6 py-4">Subject</th>
                <th className="px-6 py-4">Period</th>
                <th className="px-6 py-4">Status</th>
                <th className="px-6 py-4 text-right">Actions</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-100">
              {filteredRecords.map((record) => {
                const student = students.find(s => s.id === record.studentId);
                return (
                  <tr key={record.id} className="hover:bg-gray-50 group">
                    <td className="px-6 py-4">
                      <div className="font-medium text-gray-800">{student?.name || 'Unknown'}</div>
                      <div className="text-xs text-gray-500">{student?.rollNumber}</div>
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-600">{record.subject}</td>
                    <td className="px-6 py-4 text-sm font-semibold text-gray-400">P{record.period}</td>
                    <td className="px-6 py-4">
                      <select
                        value={record.status}
                        onChange={(e) => onUpdate(record.id, e.target.value as AttendanceStatus)}
                        className={`text-xs font-bold px-2 py-1 rounded border outline-none ${
                          record.status === AttendanceStatus.PRESENT ? 'text-green-600 border-green-200 bg-green-50' :
                          record.status === AttendanceStatus.ABSENT ? 'text-red-600 border-red-200 bg-red-50' :
                          'text-blue-600 border-blue-200 bg-blue-50'
                        }`}
                      >
                        <option value={AttendanceStatus.PRESENT}>PRESENT</option>
                        <option value={AttendanceStatus.ABSENT}>ABSENT</option>
                        <option value={AttendanceStatus.OD}>OD</option>
                      </select>
                    </td>
                    <td className="px-6 py-4 text-right">
                      <button 
                        onClick={() => onDelete(record.id)}
                        className="p-2 text-gray-400 hover:text-red-500 rounded-lg hover:bg-red-50 opacity-0 group-hover:opacity-100 transition-opacity"
                        title="Delete record"
                      >
                        üóëÔ∏è
                      </button>
                    </td>
                  </tr>
                );
              })}
              {filteredRecords.length === 0 && (
                <tr>
                  <td colSpan={5} className="px-6 py-20 text-center text-gray-400 italic">
                    No matching records found for the selected filters.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

