import React, { useState } from 'react';
import { Student, AttendanceRecord } from '../types';

interface AdminPanelProps {
  students: Student[];
  setStudents: React.Dispatch<React.SetStateAction<Student[]>>;
  records: AttendanceRecord[];
  onDeleteRecord: (id: string) => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({ students, setStudents, records, onDeleteRecord }) => {
  const [newStudentName, setNewStudentName] = useState('');
  const [newStudentRoll, setNewStudentRoll] = useState('');
  const [isAdding, setIsAdding] = useState(false);

  const handleAddStudent = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newStudentName || !newStudentRoll) return;

    const newStudent: Student = {
      id: Math.random().toString(36).substr(2, 9),
      name: newStudentName,
      rollNumber: newStudentRoll
    };

    setStudents(prev => [...prev, newStudent]);
    setNewStudentName('');
    setNewStudentRoll('');
    setIsAdding(false);
  };

  const handleDeleteStudent = (id: string) => {
    if (confirm("Are you sure? This will not delete past attendance records.")) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  };

  return (
    <div className="space-y-8">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-800">System Management</h2>
        <button 
          onClick={() => setIsAdding(!isAdding)}
          className="px-4 py-2 bg-indigo-600 text-white rounded-lg font-semibold shadow-sm hover:bg-indigo-700 transition-colors"
        >
          {isAdding ? 'Cancel' : '+ Add New Student'}
        </button>
      </div>

      {isAdding && (
        <div className="bg-white p-6 rounded-xl shadow-md border-2 border-indigo-100 animate-in fade-in slide-in-from-top-4 duration-300">
          <form onSubmit={handleAddStudent} className="flex flex-wrap gap-4 items-end">
            <div className="flex-1 min-w-[200px]">
              <label className="block text-sm font-medium text-gray-700 mb-1">Student Name</label>
              <input 
                type="text"
                required
                className="w-full border rounded-lg p-2 outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="John Doe"
                value={newStudentName}
                onChange={(e) => setNewStudentName(e.target.value)}
              />
            </div>
            <div className="flex-1 min-w-[200px]">
              <label className="block text-sm font-medium text-gray-700 mb-1">Roll Number</label>
              <input 
                type="text"
                required
                className="w-full border rounded-lg p-2 outline-none focus:ring-2 focus:ring-indigo-500"
                placeholder="CS2024XXX"
                value={newStudentRoll}
                onChange={(e) => setNewStudentRoll(e.target.value)}
              />
            </div>
            <button 
              type="submit"
              className="px-6 py-2 bg-green-600 text-white rounded-lg font-bold hover:bg-green-700"
            >
              Add Student
            </button>
          </form>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Student Management Column */}
        <div className="md:col-span-2 space-y-4">
          <div className="bg-white rounded-xl shadow-sm border overflow-hidden">
            <div className="px-6 py-4 border-b bg-gray-50 flex justify-between items-center">
              <h3 className="font-bold text-gray-700">Student Database</h3>
              <span className="text-xs bg-indigo-100 text-indigo-700 px-2 py-1 rounded-full">{students.length} Total</span>
            </div>
            <div className="max-h-[500px] overflow-y-auto">
              <table className="w-full text-left">
                <thead className="bg-gray-50 text-xs font-semibold text-gray-500 sticky top-0 border-b">
                  <tr>
                    <th className="px-6 py-3">Student Details</th>
                    <th className="px-6 py-3 text-right">Actions</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-100">
                  {students.map(s => (
                    <tr key={s.id} className="hover:bg-gray-50 group">
                      <td className="px-6 py-3">
                        <div className="font-medium text-gray-800">{s.name}</div>
                        <div className="text-xs font-mono text-gray-500">{s.rollNumber}</div>
                      </td>
                      <td className="px-6 py-3 text-right">
                        <button 
                          onClick={() => handleDeleteStudent(s.id)}
