        {[
          { label: 'Total Students', value: students.length, trend: '+2', color: 'indigo' },
          { label: 'Avg Attendance', value: '88%', trend: '+5%', color: 'emerald' },
          { label: 'Absentees Today', value: absentCount, trend: '-1', color: 'rose' },
          { label: 'Active Subjects', value: '5', trend: '0', color: 'amber' },
        ].map((stat, i) => (
          <div key={i} className="bg-white p-6 rounded-3xl shadow-sm border border-gray-100 hover:shadow-md transition-shadow">
            <p className="text-sm font-medium text-gray-400 mb-1">{stat.label}</p>
            <div className="flex items-end justify-between">
              <h4 className="text-3xl font-bold text-gray-900">{stat.value}</h4>
              <span className={`text-xs font-bold px-2 py-1 rounded-full bg-${stat.color}-50 text-${stat.color}-600`}>
