              <div key={i} className="flex items-center justify-between">
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 rounded-full" style={{ backgroundColor: d.color }}></div>
                  <span className="text-xs font-medium text-gray-600">{d.name}</span>
                </div>
                <span className="text-xs font-bold text-gray-900">{d.value}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Activity Log */}
      <div className="bg-white rounded-3xl shadow-sm border border-gray-100 overflow-hidden">
        <div className="p-6 border-b flex items-center justify-between">
          <h3 className="font-bold text-gray-900">Recent Logs</h3>
          <button className="text-xs font-bold text-indigo-600 hover:text-indigo-700">View All</button>
        </div>
        <div className="divide-y divide-gray-50">
          {records.slice(-5).reverse().map((record, i) => {
            const student = students.find(s => s.id === record.studentId);
            return (
              <div key={i} className="px-6 py-4 flex items-center justify-between hover:bg-gray-50 transition-colors">
                <div className="flex items-center space-x-4">
                  <img src={student?.avatarUrl} className="w-10 h-10 rounded-xl bg-gray-100" alt="" />
                  <div>
                    <p className="text-sm font-bold text-gray-900">{student?.name}</p>
                    <p className="text-xs text-gray-500">Marked as {record.status} for {record.subject}</p>
                  </div>
                </div>
                <span className="text-xs text-gray-400">{record.date}</span>
              </div>
            );
          })}
          {records.length === 0 && (
            <div className="p-12 text-center text-gray-400">No activity logs found.</div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;

import React, { useState } from 'react';
import { User, Student, AttendanceRecord, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';

interface MarkAttendanceProps {
  user: User;
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const MarkAttendance: React.FC<MarkAttendanceProps> = ({ user, students, onSave }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0].name);
  const [selectedPeriod, setSelectedPeriod] = useState(1);
  const [attendance, setAttendance] = useState<Record<string, AttendanceStatus>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setAttendance(prev => ({ ...prev, [studentId]: status }));
  };

  const handleSave = async () => {
    setIsSaving(true);
    const today = new Date().toISOString().split('T')[0];
    const newRecords: AttendanceRecord[] = students.map(student => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: student.id,
      status: attendance[student.id] || AttendanceStatus.PRESENT,
      date: today,
      period: selectedPeriod,
      subject: selectedSubject,
      facultyId: user.id
    }));

    // Simulate network delay
    await new Promise(r => setTimeout(r, 800));
    onSave(newRecords);
    setIsSaving(false);
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <div className="max-w-6xl mx-auto space-y-8 animate-in fade-in duration-500">
      <div className="bg-white p-8 rounded-[2rem] shadow-sm border border-gray-100 flex flex-wrap gap-6 items-end">
        <div className="flex-1 min-w-[240px]">
          <h2 className="text-xl font-bold text-gray-900 mb-4">Class Configuration</h2>
          <div className="flex gap-4">
            <div className="flex-1">
              <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Subject</label>
              <select 
                className="w-full bg-gray-50 border-none rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500"
                value={selectedSubject}
                onChange={(e) => setSelectedSubject(e.target.value)}
              >
                {SUBJECTS.map(s => <option key={s.id} value={s.name}>{s.name}</option>)}
              </select>
            </div>
            <div className="w-32">
              <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Period</label>
              <select 
                className="w-full bg-gray-50 border-none rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500"
                value={selectedPeriod}
                onChange={(e) => setSelectedPeriod(Number(e.target.value))}
              >
                {[1, 2, 3, 4, 5, 6, 7].map(p => <option key={p} value={p}>Period {p}</option>)}
              </select>
            </div>
          </div>
        </div>
        <div className="flex space-x-2">
          <button 
            onClick={() => {
              const all: Record<string, AttendanceStatus> = {};
              students.forEach(s => all[s.id] = AttendanceStatus.PRESENT);
              setAttendance(all);
            }}
            className="px-6 py-3 bg-indigo-50 text-indigo-600 rounded-2xl text-sm font-bold hover:bg-indigo-100 transition-colors"
          >
            Mark All Present
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-6">
        {students.map((student) => {
          const status = attendance[student.id] || AttendanceStatus.PRESENT;
          return (
            <div key={student.id} className="bg-white p-6 rounded-[2rem] shadow-sm border border-gray-100 hover:shadow-xl hover:-translate-y-1 transition-all group">
              <div className="flex flex-col items-center text-center mb-6">
                <img 
                  src={student.avatarUrl} 
                  className="w-20 h-20 rounded-3xl bg-gray-50 mb-4 shadow-sm group-hover:scale-110 transition-transform" 
                  alt="" 
                />
                <h4 className="font-bold text-gray-900 line-clamp-1">{student.name}</h4>
                <p className="text-xs font-mono text-gray-400 mt-1 uppercase tracking-tighter">{student.rollNumber}</p>
              </div>

              <div className="flex items-center justify-between gap-1 p-1 bg-gray-50 rounded-2xl">
                {[
                  { id: AttendanceStatus.PRESENT, label: 'P', color: 'emerald' },
                  { id: AttendanceStatus.ABSENT, label: 'A', color: 'rose' },
                  { id: AttendanceStatus.OD, label: 'OD', color: 'amber' },
                ].map(btn => (
                  <button
                    key={btn.id}
                    onClick={() => handleStatusChange(student.id, btn.id)}
                    className={`flex-1 py-2 text-xs font-black rounded-xl transition-all ${
                      status === btn.id 
                      ? `bg-${btn.color}-600 text-white shadow-lg shadow-${btn.color}-100` 
                      : 'text-gray-400 hover:bg-white hover:text-gray-600'
                    }`}
                  >
                    {btn.label}
                  </button>
                ))}
              </div>
            </div>
          );
        })}
      </div>

      <div className="sticky bottom-8 flex justify-center">
        <div className="bg-white/80 backdrop-blur-md px-4 py-3 rounded-3xl shadow-2xl border border-gray-100 flex items-center space-x-6">
          <div className="flex space-x-4">
            <div className="flex flex-col items-center">
              <span className="text-emerald-600 font-black text-lg">
                {Object.values(attendance).filter(v => v === AttendanceStatus.PRESENT).length || students.length}
              </span>
              <span className="text-[10px] uppercase font-bold text-gray-400">Present</span>
            </div>
            <div className="flex flex-col items-center">
              <span className="text-rose-600 font-black text-lg">
                {Object.values(attendance).filter(v => v === AttendanceStatus.ABSENT).length}
              </span>
              <span className="text-[10px] uppercase font-bold text-gray-400">Absent</span>
            </div>
          </div>

          <div className="h-8 w-px bg-gray-200"></div>

          <button
            onClick={handleSave}
            disabled={isSaving}
            className="px-10 py-4 bg-indigo-600 hover:bg-indigo-700 text-white font-bold rounded-2xl shadow-xl shadow-indigo-100 transition-all active:scale-95 disabled:opacity-50 flex items-center space-x-3"
          >
            {isSaving ? (
              <span className="animate-spin text-xl">üåÄ</span>
            ) : (
              <>
                <span>Submit Attendance</span>
                <span className="text-lg">‚ûî</span>
              </>
            )}
          </button>
        </div>
      </div>

      {showSuccess && (
        <div className="fixed top-24 left-1/2 -translate-x-1/2 bg-emerald-600 text-white px-8 py-4 rounded-3xl shadow-2xl font-bold animate-in slide-in-from-top-4 duration-300 z-50">
          ‚ú® Attendance Submitted Successfully!
        </div>
      )}
    </div>
  );
};

export default MarkAttendance;

import React, { useState } from 'react';
import { AttendanceRecord, Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateAttendanceReport } from '../services/geminiService';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, onUpdate, onDelete }) => {
  const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]);
  const [filterSubject, setFilterSubject] = useState('All');
  const [viewType, setViewType] = useState<'list' | 'heatmap'>('list');
  const [aiInsight, setAiInsight] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);

  const filteredRecords = records.filter(r => {
    const matchesDate = !filterDate || r.date === filterDate;
    const matchesSubject = filterSubject === 'All' || r.subject === filterSubject;
    return matchesDate && matchesSubject;
  });

  const handleGenerateAI = async () => {
    if (filteredRecords.length === 0) {
      alert("No records to analyze for this criteria.");
      return;
    }
    setIsGenerating(true);
    setAiInsight(null);
    try {
      const insight = await generateAttendanceReport(filteredRecords, students, filterSubject, filterDate);
      setAiInsight(insight);
    } catch (e) {
      setAiInsight("Error generating report.");
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="space-y-8 max-w-7xl mx-auto">
      <div className="flex flex-col md:flex-row md:items-end justify-between gap-6">
        <div className="flex flex-wrap gap-4 items-end flex-1">
          <div className="w-full sm:w-auto min-w-[180px]">
            <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Select Date</label>
            <input 
              type="date"
              className="w-full bg-white border border-gray-100 rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500 shadow-sm"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
            />
          </div>
          <div className="w-full sm:w-auto min-w-[200px]">
            <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Subject</label>
            <select 
              className="w-full bg-white border border-gray-100 rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500 shadow-sm"
              value={filterSubject}
              onChange={(e) => setFilterSubject(e.target.value)}
            >
              <option value="All">All Subjects</option>
              {SUBJECTS.map(s => <option key={s.id} value={s.name}>{s.name}</option>)}
            </select>
          </div>
          <button 
            onClick={handleGenerateAI}
            disabled={isGenerating}
            className="h-[46px] px-6 bg-purple-600 hover:bg-purple-700 text-white rounded-2xl font-bold transition-all disabled:opacity-50 flex items-center space-x-2"
          >
            <span>‚ú®</span>
            <span>{isGenerating ? 'Analyzing...' : 'AI Analysis'}</span>
          </button>
        </div>

        <div className="flex bg-gray-100 p-1 rounded-2xl">
          <button 
            onClick={() => setViewType('list')}
            className={`px-4 py-2 rounded-xl text-xs font-bold transition-all ${viewType === 'list' ? 'bg-white text-indigo-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'}`}
          >
            List
          </button>
          <button 
            onClick={() => setViewType('heatmap')}
            className={`px-4 py-2 rounded-xl text-xs font-bold transition-all ${viewType === 'heatmap' ? 'bg-white text-indigo-600 shadow-sm' : 'text-gray-500 hover:text-gray-700'}`}
          >
            Heatmap
          </button>
        </div>
      </div>

      {aiInsight && (
        <div className="bg-gradient-to-br from-indigo-900 to-purple-900 rounded-[2rem] p-8 text-white relative overflow-hidden shadow-xl shadow-indigo-100 animate-in zoom-in-95 duration-500">
          <div className="absolute top-0 right-0 p-8 opacity-10 text-8xl">‚ú®</div>
          <button onClick={() => setAiInsight(null)} className="absolute top-4 right-4 text-white/50 hover:text-white">‚úï</button>
          <h4 className="text-xl font-bold mb-4 flex items-center">
            <span className="mr-3 p-2 bg-white/10 rounded-xl">ü§ñ</span> 
            Smart Insights Report
          </h4>
          <div className="prose prose-invert max-w-none prose-sm opacity-90 leading-relaxed">
            {aiInsight}
          </div>
        </div>
      )}

      {viewType === 'list' ? (
        <div className="bg-white rounded-[2rem] shadow-sm border border-gray-100 overflow-hidden">
          <div className="overflow-x-auto">
            <table className="w-full text-left">
              <thead className="bg-gray-50/50 text-xs font-bold text-gray-400 uppercase tracking-widest border-b border-gray-50">
                <tr>
                  <th className="px-8 py-5">Student</th>
                  <th className="px-8 py-5">Subject</th>
                  <th className="px-8 py-5">Period</th>
                  <th className="px-8 py-5">Status</th>
                  <th className="px-8 py-5 text-right">Actions</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-50">
                {filteredRecords.map((record) => {
                  const student = students.find(s => s.id === record.studentId);
                  return (
                    <tr key={record.id} className="hover:bg-gray-50/50 group transition-colors">
                      <td className="px-8 py-5">
                        <div className="flex items-center space-x-4">
                          <img src={student?.avatarUrl} className="w-10 h-10 rounded-xl bg-gray-50" alt="" />
                          <div>
                            <div className="font-bold text-gray-900">{student?.name}</div>
                            <div className="text-[10px] font-mono text-gray-400 uppercase tracking-tighter">{student?.rollNumber}</div>
                          </div>
                        </div>
                      </td>
                      <td className="px-8 py-5 text-sm font-medium text-gray-600">{record.subject}</td>
                      <td className="px-8 py-5">
                        <span className="bg-gray-100 px-3 py-1 rounded-lg text-xs font-bold text-gray-400">P{record.period}</span>
                      </td>
                      <td className="px-8 py-5">
                        <select
                          value={record.status}
                          onChange={(e) => onUpdate(record.id, e.target.value as AttendanceStatus)}
                          className={`text-xs font-black px-4 py-2 rounded-xl border-none outline-none appearance-none cursor-pointer transition-all ${
                            record.status === AttendanceStatus.PRESENT ? 'bg-emerald-50 text-emerald-600 shadow-sm shadow-emerald-50' :
                            record.status === AttendanceStatus.ABSENT ? 'bg-rose-50 text-rose-600 shadow-sm shadow-rose-50' :
                            'bg-amber-50 text-amber-600 shadow-sm shadow-amber-50'
                          }`}
                        >
                          <option value={AttendanceStatus.PRESENT}>PRESENT</option>
                          <option value={AttendanceStatus.ABSENT}>ABSENT</option>
                          <option value={AttendanceStatus.OD}>OD</option>
                        </select>
                      </td>
                      <td className="px-8 py-5 text-right">
                        <button 
                          onClick={() => onDelete(record.id)}
                          className="p-2 text-gray-300 hover:text-rose-600 hover:bg-rose-50 rounded-xl opacity-0 group-hover:opacity-100 transition-all"
                        >
                          üóëÔ∏è
                        </button>
                      </td>
                    </tr>
                  );
                })}
                {filteredRecords.length === 0 && (
                  <tr>
                    <td colSpan={5} className="px-8 py-24 text-center">
                      <div className="text-gray-300 mb-2 text-4xl">üì≠</div>
                      <p className="text-gray-400 font-medium">No records found for this selection.</p>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      ) : (
        <div className="bg-white p-8 rounded-[2rem] shadow-sm border border-gray-100">
          <div className="flex justify-between items-center mb-10">
            <h3 className="font-bold text-gray-900">Period-wise Heatmap</h3>
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 rounded bg-emerald-500"></div>
                <span className="text-[10px] uppercase font-bold text-gray-400 tracking-widest">Present</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 rounded bg-rose-500"></div>
                <span className="text-[10px] uppercase font-bold text-gray-400 tracking-widest">Absent</span>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-[180px_1fr] gap-4">
            <div className="space-y-4 pt-10">
              {students.map(s => (
                <div key={s.id} className="h-10 flex items-center">
                  <span className="text-xs font-bold text-gray-600 line-clamp-1">{s.name}</span>
                </div>
              ))}
            </div>
            <div className="overflow-x-auto">
              <div className="flex mb-4">
                {[1, 2, 3, 4, 5, 6, 7].map(p => (
                  <div key={p} className="flex-1 text-center min-w-[50px]">
                    <span className="text-[10px] uppercase font-bold text-gray-400 tracking-tighter">P{p}</span>
                  </div>
                ))}
              </div>
              <div className="space-y-4">
                {students.map(s => (
                  <div key={s.id} className="flex gap-2 h-10">
                    {[1, 2, 3, 4, 5, 6, 7].map(p => {
                      const rec = filteredRecords.find(r => r.studentId === s.id && r.period === p);
                      let bgColor = 'bg-gray-50';
                      if (rec?.status === AttendanceStatus.PRESENT) bgColor = 'bg-emerald-500 shadow-lg shadow-emerald-100';
                      if (rec?.status === AttendanceStatus.ABSENT) bgColor = 'bg-rose-500 shadow-lg shadow-rose-100';
                      if (rec?.status === AttendanceStatus.OD) bgColor = 'bg-amber-500 shadow-lg shadow-amber-100';

                      return (
                        <div 
                          key={p} 
                          className={`flex-1 min-w-[50px] rounded-xl ${bgColor} transition-all cursor-help hover:scale-105`}
                          title={rec ? `${s.name}: ${rec.status}` : 'Not Marked'}
                        />
                      );
                    })}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

