import React, { useState, useEffect } from 'react';
import { User, UserRole, Student, AttendanceRecord, AttendanceStatus } from './types';
import { INITIAL_STUDENTS, MOCK_FACULTY, MOCK_ADMIN, SUBJECTS } from './constants';
import Layout from './components/Layout';
import AuthView from './views/AuthView';
import Dashboard from './views/Dashboard';
import MarkAttendance from './views/MarkAttendance';
import AttendanceHistory from './views/AttendanceHistory';
import AdminPanel from './views/AdminPanel';

const App: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [activeView, setActiveView] = useState('dashboard');
  const [students, setStudents] = useState<Student[]>([]);
  const [records, setRecords] = useState<AttendanceRecord[]>([]);

  // Load initial data
  useEffect(() => {
    const storedUser = localStorage.getItem('sams_user');
    const storedRecords = localStorage.getItem('sams_records');
    const storedStudents = localStorage.getItem('sams_students');

    if (storedUser) setUser(JSON.parse(storedUser));

    if (storedRecords) {
      setRecords(JSON.parse(storedRecords));
    } else {
      setRecords([]);
    }

    if (storedStudents) {
      setStudents(JSON.parse(storedStudents));
    } else {
      setStudents(INITIAL_STUDENTS);
    }
  }, []);

  // Save data to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('sams_records', JSON.stringify(records));
  }, [records]);

  useEffect(() => {
    localStorage.setItem('sams_students', JSON.stringify(students));
  }, [students]);

  const handleLogin = (u: User) => {
    setUser(u);
    localStorage.setItem('sams_user', JSON.stringify(u));
  };

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('sams_user');
  };

  const addRecord = (newRecords: AttendanceRecord[]) => {
    setRecords(prev => [...prev, ...newRecords]);
  };

  const updateRecord = (recordId: string, status: AttendanceStatus) => {
    setRecords(prev => prev.map(r => r.id === recordId ? { ...r, status } : r));
  };

  const deleteRecord = (recordId: string) => {
    setRecords(prev => prev.filter(r => r.id !== recordId));
  };

  if (!user) {
    return <AuthView onLogin={handleLogin} />;
  }

  return (
    <Layout 
      user={user} 
      onLogout={handleLogout} 
      activeView={activeView} 
      setActiveView={setActiveView}
    >
      {activeView === 'dashboard' && (
        <Dashboard user={user} records={records} students={students} setActiveView={setActiveView} />
      )}
      {activeView === 'mark' && (
        <MarkAttendance 
          user={user} 
          students={students} 
          onSave={addRecord} 
        />
      )}
      {activeView === 'history' && (
        <AttendanceHistory 
          records={records} 
          students={students} 
          onUpdate={updateRecord}
          onDelete={deleteRecord}
        />
      )}
      {activeView === 'admin' && user.role === UserRole.ADMIN && (
        <AdminPanel 
          students={students} 
          setStudents={setStudents}
          records={records}
          onDeleteRecord={deleteRecord}
        />
      )}
    </Layout>
  );
};

export default App;

