import React, { useState, useMemo } from 'react';
import { User, Student, AttendanceRecord, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';

interface MarkAttendanceProps {
  user: User;
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const MarkAttendance: React.FC<MarkAttendanceProps> = ({ user, students, onSave }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0].name);
  const [selectedPeriod, setSelectedPeriod] = useState(3);
  const [attendance, setAttendance] = useState<Record<string, AttendanceStatus>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<'All' | 'Present' | 'Absent'>('All');

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setAttendance(prev => ({ ...prev, [studentId]: status }));
  };

  const handleSave = async () => {
    setIsSaving(true);
    const today = new Date().toISOString().split('T')[0];
    const newRecords: AttendanceRecord[] = students.map(student => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: student.id,
      status: attendance[student.id] || AttendanceStatus.PRESENT,
      date: today,
      period: selectedPeriod,
      subject: selectedSubject,
      facultyId: user.id
    }));

    await new Promise(r => setTimeout(r, 800));
    onSave(newRecords);
    setIsSaving(false);
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 4000);
  };

  const filteredStudents = useMemo(() => {
    return students.filter(s => {
      const matchesSearch = s.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            s.rollNumber.toLowerCase().includes(searchQuery.toLowerCase());

      const currentStatus = attendance[s.id] || AttendanceStatus.PRESENT;
      const matchesFilter = filterStatus === 'All' || 
                            (filterStatus === 'Present' && currentStatus === AttendanceStatus.PRESENT) ||
                            (filterStatus === 'Absent' && currentStatus === AttendanceStatus.ABSENT);

      return matchesSearch && matchesFilter;
    });
  }, [students, searchQuery, filterStatus, attendance]);

  return (
    <div className="flex flex-col h-full bg-[#F8FAFC] animate-in fade-in duration-500">
      {/* Top Action Bar */}
      <div className="flex items-center justify-between mb-8 px-2">
        <div className="flex items-center space-x-8">
          <div>
            <h1 className="text-xl font-black text-slate-800 tracking-tight">B.Tech CS - Sec A</h1>
            <p className="text-slate-400 text-sm font-medium">June 12, 2024 ‚Ä¢ Period 3</p>
          </div>
          <div className="relative">
            <span className="absolute inset-y-0 left-4 flex items-center text-slate-400">
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path></svg>
            </span>
            <input 
              type="text" 
              placeholder="Search by name or roll no..." 
              className="pl-12 pr-6 py-2.5 bg-white border border-slate-200 rounded-xl w-80 text-sm focus:ring-2 focus:ring-blue-500 outline-none shadow-sm transition-all"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
          </div>
        </div>

        <div className="flex items-center space-x-6">
          <div className="bg-slate-100/50 p-1 rounded-xl flex items-center">
            <button 
              onClick={() => setFilterStatus('All')}
              className={`px-6 py-2 rounded-lg text-xs font-bold transition-all ${filterStatus === 'All' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
            >
              All ({students.length})
            </button>
            <button 
              onClick={() => setFilterStatus('Present')}
              className={`px-6 py-2 rounded-lg text-xs font-bold transition-all ${filterStatus === 'Present' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
            >
              Present
            </button>
            <button 
              onClick={() => setFilterStatus('Absent')}
              className={`px-6 py-2 rounded-lg text-xs font-bold transition-all ${filterStatus === 'Absent' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
            >
              Absent
            </button>
          </div>
          <button 
            onClick={handleSave}
            disabled={isSaving}
            className="bg-[#3B82F6] text-white px-8 py-3 rounded-xl font-bold text-xs uppercase tracking-widest flex items-center space-x-3 hover:bg-blue-700 shadow-lg shadow-blue-100 transition-all active:scale-95 disabled:opacity-50"
          >
            {isSaving ? <span className="animate-spin text-lg">üåÄ</span> : <svg className="w-4 h-4" fill="currentColor" viewBox="0 0 20 20"><path d="M7.707 10.293a1 1 0 10-1.414 1.414l3 3a1 1 0 001.414 0l3-3a1 1 0 00-1.414-1.414L11 11.586V4a1 1 0 10-2 0v7.586l-1.293-1.293z"></path><path d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z"></path></svg>}
            <span>Finalize List</span>
          </button>
        </div>
      </div>

      {/* List Table */}
      <div className="bg-white rounded-[2rem] border border-slate-100 shadow-sm overflow-hidden mb-12">
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead className="bg-slate-50/50">
              <tr className="border-b border-slate-50">
                <th className="px-8 py-6 text-[11px] font-black text-slate-400 uppercase tracking-widest">Roll No</th>
                <th className="px-8 py-6 text-[11px] font-black text-slate-400 uppercase tracking-widest">Student Name</th>
                <th className="px-8 py-6 text-[11px] font-black text-slate-400 uppercase tracking-widest text-center">Mark Attendance</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {filteredStudents.map((student) => {
                const status = attendance[student.id] || AttendanceStatus.PRESENT;

                let rowBg = 'bg-white';
                if (status === AttendanceStatus.PRESENT) rowBg = 'bg-emerald-50/30';
                if (status === AttendanceStatus.ABSENT) rowBg = 'bg-rose-50/30';
                if (status === AttendanceStatus.OD) rowBg = 'bg-blue-50/30';

                return (
                  <tr key={student.id} className={`${rowBg} transition-colors group`}>
                    <td className="px-8 py-5 text-sm font-bold text-slate-600 font-mono tracking-tight">{student.rollNumber}</td>
                    <td className="px-8 py-5">
                      <div className="flex items-center space-x-4">
                        <div className="w-9 h-9 rounded-full bg-slate-100 flex items-center justify-center text-slate-400 text-xs font-bold uppercase">
                          {student.name.charAt(0)}
                        </div>
                        <span className="text-sm font-bold text-slate-800">{student.name}</span>
                      </div>
                    </td>
                    <td className="px-8 py-5">
                      <div className="flex justify-center space-x-3">
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                          className={`w-9 h-9 rounded-full flex items-center justify-center text-[11px] font-black border transition-all ${
                            status === AttendanceStatus.PRESENT 
                            ? 'bg-[#10B981] border-[#10B981] text-white shadow-lg shadow-emerald-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-300'
                          }`}
                        >
                          P
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                          className={`w-9 h-9 rounded-full flex items-center justify-center text-[11px] font-black border transition-all ${
                            status === AttendanceStatus.ABSENT 
                            ? 'bg-[#EF4444] border-[#EF4444] text-white shadow-lg shadow-rose-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-300'
                          }`}
                        >
                          A
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                          className={`w-9 h-9 rounded-full flex items-center justify-center text-[11px] font-black border transition-all ${
                            status === AttendanceStatus.OD 
                            ? 'bg-[#3B82F6] border-[#3B82F6] text-white shadow-lg shadow-blue-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-300'
                          }`}
                        >
                          OD
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          {filteredStudents.length === 0 && (
            <div className="py-24 text-center">
              <div className="text-slate-200 mb-4 text-4xl">üîç</div>
              <p className="text-slate-400 font-bold">No students match your criteria.</p>
            </div>
          )}
        </div>
      </div>

      {showSuccess && (
        <div className="fixed bottom-10 left-10 animate-in slide-in-from-left-8 duration-500 z-[70]">
          <div className="bg-[#0F172A] text-white px-8 py-6 rounded-3xl shadow-2xl flex items-center space-x-6">
            <div className="w-10 h-10 bg-[#10B981] rounded-xl flex items-center justify-center">
              <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7"></path></svg>
            </div>
            <span className="font-bold tracking-tight text-lg">Attendance saved</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default MarkAttendance;

import React, { useState } from 'react';
import { AttendanceRecord, Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateAttendanceReport } from '../services/geminiService';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, onUpdate, onDelete }) => {
  const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]);
  const [filterSubject, setFilterSubject] = useState('All');
  const [viewType, setViewType] = useState<'list' | 'heatmap'>('list');
  const [aiInsight, setAiInsight] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);

  const filteredRecords = records.filter(r => {
    const matchesDate = !filterDate || r.date === filterDate;
    const matchesSubject = filterSubject === 'All' || r.subject === filterSubject;
    return matchesDate && matchesSubject;
  });

  const handleGenerateAI = async () => {
    if (filteredRecords.length === 0) {
      alert("No records to analyze for this criteria.");
      return;
    }
    setIsGenerating(true);
    setAiInsight(null);
    try {
      const insight = await generateAttendanceReport(filteredRecords, students, filterSubject, filterDate);
      setAiInsight(insight);
    } catch (e) {
      setAiInsight("Error generating report.");
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="space-y-8 max-w-7xl mx-auto">
      <div className="flex flex-col md:flex-row md:items-end justify-between gap-6">
        <div className="flex flex-wrap gap-4 items-end flex-1">
          <div className="w-full sm:w-auto min-w-[180px]">
            <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Select Date</label>
            <input 
              type="date"
              className="w-full bg-white border border-gray-100 rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500 shadow-sm"
              value={filterDate}
              onChange={(e) => setFilterDate(e.target.value)}
            />
          </div>
          <div className="w-full sm:w-auto min-w-[200px]">
            <label className="block text-xs font-bold text-gray-400 uppercase tracking-widest mb-2">Subject</label>
            <select 
              className="w-full bg-white border border-gray-100 rounded-2xl px-4 py-3 text-sm font-semibold outline-none focus:ring-2 focus:ring-indigo-500 shadow-sm"
              value={filterSubject}
              onChange={(e) => setFilterSubject(e.target.value)}
            >
              <option value="All">All Subjects</option>
              {SUBJECTS.map(s => <option key={s.id} value={s.name}>{s.name}</option>)}
            </select>
