import React, { useState, useRef } from 'react';
import { Student, AttendanceRecord } from '../types';

interface AdminPanelProps {
  students: Student[];
  setStudents: React.Dispatch<React.SetStateAction<Student[]>>;
  records: AttendanceRecord[];
  onDeleteRecord: (id: string) => void;
}

const AdminPanel: React.FC<AdminPanelProps> = ({ students, setStudents, records, onDeleteRecord }) => {
  const [newStudentName, setNewStudentName] = useState('');
  const [newStudentRoll, setNewStudentRoll] = useState('');
  const [isAdding, setIsAdding] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleAddStudent = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newStudentName || !newStudentRoll) return;

    const newStudent: Student = {
      id: Math.random().toString(36).substr(2, 9),
      name: newStudentName,
      rollNumber: newStudentRoll
    };

    setStudents(prev => [...prev, newStudent]);
    setNewStudentName('');
    setNewStudentRoll('');
    setIsAdding(false);
  };

  const handleImport = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const content = event.target?.result as string;
        let imported: Student[] = [];

        if (file.name.endsWith('.json')) {
          imported = JSON.parse(content);
        } else if (file.name.endsWith('.csv')) {
          const lines = content.split('\n');
          imported = lines.slice(1).filter(l => l.trim()).map(line => {
            const [name, roll, dept] = line.split(',').map(s => s.trim());
            return {
              id: Math.random().toString(36).substr(2, 9),
              name,
              rollNumber: roll,
              department: dept
            };
          });
        }

        if (imported.length > 0) {
          setStudents(prev => [...prev, ...imported]);
          alert(`Successfully imported ${imported.length} students.`);
        }
      } catch (err) {
        alert("Error parsing file. Ensure it is a valid JSON or CSV.");
      }
    };
    reader.readAsText(file);
  };

  const handleDeleteStudent = (id: string) => {
    if (confirm("Are you sure? This will not delete past attendance records.")) {
      setStudents(prev => prev.filter(s => s.id !== id));
    }
  };

  return (
    <div className="space-y-8 animate-in fade-in duration-500">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-2xl font-black text-gray-800 tracking-tight">System Management</h2>
          <p className="text-gray-400 text-sm font-medium">Manage student records and data imports</p>
        </div>
        <div className="flex space-x-4">
          <input 
            type="file" 
            ref={fileInputRef} 
            onChange={handleImport} 
            accept=".json,.csv" 
            className="hidden" 
          />
          <button 
            onClick={() => fileInputRef.current?.click()}
            className="px-6 py-3 bg-white border border-slate-200 text-slate-600 rounded-2xl font-bold shadow-sm hover:bg-slate-50 transition-all flex items-center space-x-2"
          >
            <span>üì•</span>
            <span>Import Dataset</span>
          </button>
          <button 
            onClick={() => setIsAdding(!isAdding)}
            className="px-6 py-3 bg-blue-600 text-white rounded-2xl font-bold shadow-lg shadow-blue-100 hover:bg-blue-700 transition-all active:scale-95"
          >
            {isAdding ? 'Cancel' : '+ Add New Student'}
          </button>
        </div>
      </div>

      {isAdding && (
        <div className="bg-white p-8 rounded-[2rem] shadow-xl border border-blue-100 animate-in fade-in slide-in-from-top-4 duration-300">
          <form onSubmit={handleAddStudent} className="flex flex-wrap gap-6 items-end">
