import React, { useState, useMemo } from 'react';
import { User, Student, AttendanceRecord, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';

interface MarkAttendanceProps {
  user: User;
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const MarkAttendance: React.FC<MarkAttendanceProps> = ({ user, students, onSave }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0].name);
  const [selectedPeriod, setSelectedPeriod] = useState(1);
  const [attendance, setAttendance] = useState<Record<string, AttendanceStatus>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<'All' | 'Present' | 'Absent'>('All');

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setAttendance(prev => ({ ...prev, [studentId]: status }));
  };

  const handleSave = async () => {
    // Only save if some attendance has been marked, or just save all as default PRESENT
    setIsSaving(true);
    const today = new Date().toISOString().split('T')[0];
    const newRecords: AttendanceRecord[] = students.map(student => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: student.id,
      status: attendance[student.id] || AttendanceStatus.PRESENT, // Default to Present
      date: today,
      period: selectedPeriod,
      subject: selectedSubject,
      facultyId: user.id
    }));

    // Artificial delay for UX
    await new Promise(r => setTimeout(r, 800));
    onSave(newRecords);
    setIsSaving(false);
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 4000);
  };

  const filteredStudents = useMemo(() => {
    return students.filter(s => {
      const matchesSearch = s.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            s.rollNumber.toLowerCase().includes(searchQuery.toLowerCase());

      const currentStatus = attendance[s.id] || AttendanceStatus.PRESENT;
      const matchesFilter = filterStatus === 'All' || 
                            (filterStatus === 'Present' && currentStatus === AttendanceStatus.PRESENT) ||
                            (filterStatus === 'Absent' && currentStatus === AttendanceStatus.ABSENT);

      return matchesSearch && matchesFilter;
    });
  }, [students, searchQuery, filterStatus, attendance]);

  const periods = [1, 2, 3, 4, 5, 6, 7];

  return (
    <div className="flex flex-col h-full bg-[#F8FAFC] animate-in fade-in duration-500 pb-20">
      {/* Top Header Section */}
      <div className="flex flex-col space-y-8 mb-10 px-2">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-black text-slate-800 tracking-tight">B.Tech CS - Sec A</h1>
            <p className="text-slate-400 text-sm font-medium">Marking attendance for today</p>
          </div>
          <button 
            onClick={handleSave}
            disabled={isSaving}
            className="bg-[#3B82F6] text-white px-10 py-3.5 rounded-2xl font-bold text-xs uppercase tracking-widest flex items-center space-x-3 hover:bg-blue-700 shadow-xl shadow-blue-100 transition-all active:scale-95 disabled:opacity-50"
          >
            {isSaving ? <span className="animate-spin text-lg">üåÄ</span> : <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M7.707 10.293a1 1 0 10-1.414 1.414l3 3a1 1 0 001.414 0l3-3a1 1 0 00-1.414-1.414L11 11.586V4a1 1 0 10-2 0v7.586l-1.293-1.293z"></path><path d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z"></path></svg>}
            <span>Submit to Admin Dashboard</span>
          </button>
        </div>

        {/* Interactive Selectors */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col space-y-6">
            <span className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Select Period</span>
            <div className="flex items-center space-x-4">
              {periods.map((p) => (
                <button
                  key={p}
                  onClick={() => setSelectedPeriod(p)}
                  className={`w-12 h-12 rounded-2xl flex items-center justify-center font-black transition-all border-2 ${
                    selectedPeriod === p 
                    ? 'bg-blue-600 border-blue-600 text-white shadow-xl shadow-blue-100' 
                    : 'bg-slate-50 border-transparent text-slate-400 hover:bg-slate-100'
                  }`}
                >
                  P{p}
                </button>
              ))}
            </div>
          </div>

          <div className="bg-white p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col space-y-6">
            <span className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Select Subject</span>
            <div className="flex items-center space-x-3 overflow-x-auto pb-2 scrollbar-hide">
              {SUBJECTS.map((s) => (
                <button
                  key={s.id}
                  onClick={() => setSelectedSubject(s.name)}
                  className={`px-6 py-3 rounded-2xl font-bold text-xs whitespace-nowrap transition-all border-2 ${
                    selectedSubject === s.name 
                    ? 'bg-blue-50 border-blue-200 text-blue-600' 
                    : 'bg-slate-50 border-transparent text-slate-500 hover:bg-slate-100'
                  }`}
                >
                  {s.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Filter and Search Bar */}
      <div className="flex items-center justify-between mb-8 px-2">
        <div className="relative">
          <span className="absolute inset-y-0 left-4 flex items-center text-slate-400">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path></svg>
          </span>
          <input 
            type="text" 
            placeholder="Search by name or roll no..." 
            className="pl-12 pr-6 py-3.5 bg-white border border-slate-200 rounded-2xl w-96 text-sm focus:ring-2 focus:ring-blue-500 outline-none shadow-sm transition-all"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <div className="bg-slate-100/50 p-1.5 rounded-2xl flex items-center shadow-inner">
          <button 
            onClick={() => setFilterStatus('All')}
            className={`px-8 py-2.5 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${filterStatus === 'All' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
          >
            All Students ({students.length})
          </button>
          <button 
            onClick={() => setFilterStatus('Present')}
            className={`px-8 py-2.5 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${filterStatus === 'Present' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
          >
            Present
          </button>
          <button 
            onClick={() => setFilterStatus('Absent')}
            className={`px-8 py-2.5 rounded-xl text-xs font-black uppercase tracking-widest transition-all ${filterStatus === 'Absent' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-600'}`}
          >
            Absent
          </button>
        </div>
      </div>

      {/* List Table */}
      <div className="bg-white rounded-[3rem] border border-slate-100 shadow-sm overflow-hidden mb-12">
        <div className="overflow-x-auto">
          <table className="w-full text-left">
            <thead className="bg-slate-50/50">
              <tr className="border-b border-slate-100">
                <th className="px-10 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em]">Roll No</th>
                <th className="px-10 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em]">Student Name</th>
                <th className="px-10 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em] text-center">Action Toggle</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {filteredStudents.map((student) => {
                const status = attendance[student.id] || AttendanceStatus.PRESENT;

                let rowBg = 'bg-white';
                if (status === AttendanceStatus.PRESENT) rowBg = 'bg-emerald-50/10';
                if (status === AttendanceStatus.ABSENT) rowBg = 'bg-rose-50/10';
                if (status === AttendanceStatus.OD) rowBg = 'bg-blue-50/10';

                return (
                  <tr key={student.id} className={`${rowBg} transition-colors group`}>
                    <td className="px-10 py-6 text-sm font-bold text-slate-600 font-mono">{student.rollNumber}</td>
                    <td className="px-10 py-6">
                      <div className="flex items-center space-x-5">
                        <div className="w-10 h-10 rounded-2xl bg-slate-100 flex items-center justify-center text-slate-400 text-xs font-black shadow-inner">
                          {student.name.charAt(0)}
                        </div>
                        <span className="text-sm font-bold text-slate-800 tracking-tight">{student.name}</span>
                      </div>
                    </td>
                    <td className="px-10 py-6">
                      <div className="flex justify-center space-x-4">
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                          className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.PRESENT 
                            ? 'bg-[#10B981] border-[#10B981] text-white shadow-xl shadow-emerald-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          P
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                          className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.ABSENT 
                            ? 'bg-[#EF4444] border-[#EF4444] text-white shadow-xl shadow-rose-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          A
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                          className={`w-11 h-11 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.OD 
                            ? 'bg-[#3B82F6] border-[#3B82F6] text-white shadow-xl shadow-blue-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          OD
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
          {filteredStudents.length === 0 && (
            <div className="py-32 text-center">
              <div className="text-slate-100 mb-6 text-6xl">üîç</div>
              <p className="text-slate-400 font-bold">No students found for this search.</p>
            </div>
          )}
        </div>
      </div>

      {/* Success Toast at Bottom Left */}
      {showSuccess && (
        <div className="fixed bottom-10 left-10 animate-in slide-in-from-left-8 duration-500 z-[70]">
          <div className="bg-[#0F172A] text-white px-10 py-6 rounded-[2.5rem] shadow-2xl flex items-center space-x-6 border border-white/10">
            <div className="w-12 h-12 bg-[#10B981] rounded-2xl flex items-center justify-center shadow-lg shadow-emerald-900/50">
              <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7"></path></svg>
            </div>
            <div>
              <p className="font-black tracking-tight text-lg">Attendance Synced</p>
              <p className="text-slate-400 text-xs font-bold uppercase tracking-widest">Saved to Admin Dashboard</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

