import React, { useState } from 'react';
import { AttendanceRecord, Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';
import { generateAttendanceReport } from '../services/geminiService';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, onUpdate, onDelete }) => {
  const [filterDate, setFilterDate] = useState(new Date().toISOString().split('T')[0]);
  const [filterSubject, setFilterSubject] = useState('All');
  const [viewType, setViewType] = useState<'list' | 'heatmap'>('list');
  const [aiInsight, setAiInsight] = useState<string | null>(null);
  const [isGenerating, setIsGenerating] = useState(false);

  const getDayName = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { weekday: 'long' });
  };

  const formatDate = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { 
      day: 'numeric', 
      month: 'short', 
      year: 'numeric' 
    });
  };

  const filteredRecords = records.filter(r => {
    const matchesDate = !filterDate || r.date === filterDate;
    const matchesSubject = filterSubject === 'All' || r.subject === filterSubject;
    return matchesDate && matchesSubject;
  });

  const handleGenerateAI = async () => {
    if (filteredRecords.length === 0) {
      alert("No records to analyze for this criteria.");
      return;
    }
    setIsGenerating(true);
    setAiInsight(null);
    try {
      const insight = await generateAttendanceReport(filteredRecords, students, filterSubject, filterDate);
