                  <div key={s.id} className="flex gap-3 h-12">
                    {[1, 2, 3, 4, 5, 6, 7].map(p => {
                      const rec = filteredRecords.find(r => r.studentId === s.id && r.period === p);
                      let bgColor = 'bg-slate-50';
                      let shadow = '';
                      if (rec?.status === AttendanceStatus.PRESENT) {
                        bgColor = 'bg-emerald-500';
                        shadow = 'shadow-lg shadow-emerald-100';
                      }
                      if (rec?.status === AttendanceStatus.ABSENT) {
                        bgColor = 'bg-rose-500';
                        shadow = 'shadow-lg shadow-rose-100';
                      }
                      if (rec?.status === AttendanceStatus.OD) {
                        bgColor = 'bg-blue-500';
                        shadow = 'shadow-lg shadow-blue-100';
                      }

                      return (
                        <div 
                          key={p} 
                          className={`flex-1 min-w-[60px] rounded-2xl ${bgColor} ${shadow} transition-all cursor-help hover:scale-105 group relative flex items-center justify-center`}
                          title={rec ? `${s.name}: ${rec.status}` : 'Not Marked'}
                        >
                           {rec && <span className="text-[8px] font-black text-white opacity-40 uppercase">{rec.status.charAt(0)}</span>}
                        </div>
                      );
                    })}
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

