import React, { useState, useMemo } from 'react';
import { AttendanceRecord, Student, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, onUpdate, onDelete }) => {
  const [selectedDate, setSelectedDate] = useState<string | null>(
    records.length > 0 ? [...new Set(records.map(r => r.date))].sort().reverse()[0] : null
  );

  const getDayName = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { weekday: 'long' }).toUpperCase();
  };

  const formatDateLabel = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    });
  };

  // Group records by date for the "Folders" sidebar
  const datesList = useMemo(() => {
    return [...new Set(records.map(r => r.date))].sort().reverse();
  }, [records]);

  const filteredRecords = useMemo(() => {
    return records.filter(r => r.date === selectedDate);
  }, [records, selectedDate]);

  return (
    <div className="flex flex-col lg:flex-row gap-8 h-full animate-in fade-in duration-500">
      {/* Date "Folders" Sidebar */}
      <div className="w-full lg:w-72 flex-shrink-0">
        <div className="bg-white rounded-[2rem] shadow-sm border border-slate-100 p-6">
          <h3 className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-6 px-4">Attendance Logs</h3>
          <div className="space-y-2 max-h-[calc(100vh-300px)] overflow-y-auto pr-2 scrollbar-hide">
            {datesList.length > 0 ? datesList.map((date) => (
              <button
                key={date}
                onClick={() => setSelectedDate(date)}
                className={`w-full text-left px-5 py-4 rounded-2xl transition-all flex flex-col space-y-1 ${
                  selectedDate === date 
                  ? 'bg-blue-600 text-white shadow-lg shadow-blue-100' 
                  : 'bg-white text-slate-600 hover:bg-slate-50'
                }`}
              >
                <span className="text-sm font-bold">{formatDateLabel(date)}</span>
                <span className={`text-[9px] font-black uppercase tracking-widest ${selectedDate === date ? 'text-blue-100' : 'text-slate-400'}`}>
                  {getDayName(date)}
                </span>
              </button>
            )) : (
              <p className="text-slate-400 text-xs text-center py-10 font-medium">No records yet.</p>
            )}
          </div>
        </div>
      </div>

      {/* Main List View (Matching Screenshot) */}
      <div className="flex-1 min-w-0">
        <div className="bg-white rounded-[2.5rem] shadow-sm border border-slate-100 overflow-hidden min-h-[600px]">
          <div className="overflow-x-auto">
            <table className="w-full text-left min-w-[800px]">
              <tbody className="divide-y divide-slate-50">
                {filteredRecords.length > 0 ? filteredRecords.map((record) => {
                  const student = students.find(s => s.id === record.studentId);

                  const statusStyles = {
                    [AttendanceStatus.PRESENT]: "bg-emerald-50 text-emerald-600",
                    [AttendanceStatus.ABSENT]: "bg-rose-50 text-rose-600",
                    [AttendanceStatus.OD]: "bg-blue-50 text-blue-600"
                  };

                  return (
                    <tr key={record.id} className="hover:bg-slate-50/50 transition-colors group">
                      {/* Date Column */}
                      <td className="pl-10 py-8 w-48">
                        <div className="flex flex-col">
                          <span className="text-sm font-black text-slate-800 tracking-tight">{formatDateLabel(record.date)}</span>
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest">{getDayName(record.date)}</span>
                        </div>
                      </td>

                      {/* Avatar & Student Details */}
                      <td className="px-6 py-8">
                        <div className="flex items-center space-x-5">
                          <div className="w-10 h-10 rounded-full bg-slate-100 flex items-center justify-center text-slate-400 text-[10px] font-black shadow-inner">
                            {student?.name.charAt(0).toUpperCase()}
                          </div>
                          <div className="flex flex-col">
                            <span className="text-sm font-black text-slate-800 tracking-tight">{student?.name}</span>
                            <span className="text-[10px] font-mono font-bold text-slate-400 uppercase tracking-tighter">{student?.rollNumber}</span>
                          </div>
                        </div>
                      </td>

                      {/* Subject & Period Details */}
                      <td className="px-6 py-8">
                        <div className="flex flex-col">
                          <span className="text-xs font-black text-slate-800 tracking-tight">{record.subject}</span>
                          <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest">PERIOD {record.period}</span>
                        </div>
                      </td>

                      {/* Status Badge */}
                      <td className="px-6 py-8 text-center">
                        <div className={`inline-flex px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest ${statusStyles[record.status]}`}>
                          {record.status}
                        </div>
                      </td>

                      {/* Actions Column */}
                      <td className="pr-10 py-8 text-right">
                        <button 
                          onClick={() => onDelete(record.id)}
                          className="p-3 text-slate-200 hover:text-rose-500 transition-all opacity-0 group-hover:opacity-100"
                        >
                          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"></path></svg>
                        </button>
                      </td>
                    </tr>
                  );
                }) : (
                  <tr>
                    <td className="py-48 text-center">
                      <div className="flex flex-col items-center">
                        <div className="w-20 h-20 bg-slate-50 rounded-full flex items-center justify-center text-slate-200 mb-6">
                           <svg className="w-10 h-10" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 19a2 2 0 01-2-2V7a2 2 0 012-2h4l2 2h4a2 2 0 012 2v1M5 19h14a2 2 0 002-2v-5a2 2 0 00-2-2H9l-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"></path></svg>
                        </div>
                        <p className="text-slate-400 font-black uppercase tracking-widest text-[10px]">Select a date folder to view records</p>
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

