                          className={`flex-1 max-w-[120px] py-3 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.PRESENT 
                            ? 'bg-[#10B981] border-[#10B981] text-white shadow-xl shadow-emerald-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          PRESENT
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                          className={`flex-1 max-w-[120px] py-3 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.ABSENT 
                            ? 'bg-[#EF4444] border-[#EF4444] text-white shadow-xl shadow-rose-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          ABSENT
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                          className={`flex-1 max-w-[120px] py-3 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.OD 
                            ? 'bg-[#3B82F6] border-[#3B82F6] text-white shadow-xl shadow-blue-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200'
                          }`}
                        >
                          OD
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>

      {showSuccess && (
        <div className="fixed bottom-10 left-10 animate-in slide-in-from-left-8 duration-500 z-[70]">
          <div className="bg-[#0F172A] text-white px-10 py-6 rounded-[2.5rem] shadow-2xl flex items-center space-x-6 border border-white/10">
            <div className="w-12 h-12 bg-[#10B981] rounded-2xl flex items-center justify-center shadow-lg shadow-emerald-900/50">
              <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7"></path></svg>
            </div>
            <div>
              <p className="font-black tracking-tight text-lg">EE Attendance Saved</p>
              <p className="text-slate-400 text-xs font-bold uppercase tracking-widest">Database Updated</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MarkAttendance;

import React, { useState, useMemo } from 'react';
import { AttendanceRecord, Student, AttendanceStatus } from '../types';

interface AttendanceHistoryProps {
  records: AttendanceRecord[];
  students: Student[];
  onUpdate: (id: string, status: AttendanceStatus) => void;
  onDelete: (id: string) => void;
}

const AttendanceHistory: React.FC<AttendanceHistoryProps> = ({ records, students, onUpdate, onDelete }) => {
  const [selectedDate, setSelectedDate] = useState<string | null>(
    records.length > 0 ? [...new Set(records.map(r => r.date))].sort().reverse()[0] : null
  );

  const getDayName = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { weekday: 'long' }).toUpperCase();
  };

  const formatDateLabel = (dateStr: string) => {
    return new Date(dateStr).toLocaleDateString('en-US', { 
      month: 'short', 
      day: 'numeric', 
      year: 'numeric' 
    });
  };

  const datesList = useMemo(() => {
    return [...new Set(records.map(r => r.date))].sort().reverse();
  }, [records]);

  const filteredRecords = useMemo(() => {
    return records.filter(r => r.date === selectedDate);
  }, [records, selectedDate]);

  return (
    <div className="flex flex-col lg:flex-row gap-8 h-full animate-in fade-in duration-500">
      <div className="w-full lg:w-72 flex-shrink-0">
        <div className="bg-white rounded-[2rem] shadow-sm border border-slate-100 p-6">
          <h3 className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em] mb-6 px-4">EE Attendance Folders</h3>
          <div className="space-y-2 max-h-[calc(100vh-300px)] overflow-y-auto pr-2 scrollbar-hide">
            {datesList.length > 0 ? datesList.map((date) => (
              <button
                key={date}
                onClick={() => setSelectedDate(date)}
                className={`w-full text-left px-5 py-4 rounded-2xl transition-all flex flex-col space-y-1 ${
                  selectedDate === date 
                  ? 'bg-blue-600 text-white shadow-lg shadow-blue-100' 
                  : 'bg-white text-slate-600 hover:bg-slate-50'
                }`}
              >
                <span className="text-sm font-bold">{formatDateLabel(date)}</span>
                <span className={`text-[9px] font-black uppercase tracking-widest ${selectedDate === date ? 'text-blue-100' : 'text-slate-400'}`}>
                  {getDayName(date)}
                </span>
              </button>
            )) : (
              <p className="text-slate-400 text-xs text-center py-10 font-medium">No EE records found.</p>
            )}
          </div>
        </div>
      </div>

      <div className="flex-1 min-w-0">
        <div className="bg-white rounded-[2.5rem] shadow-sm border border-slate-100 overflow-hidden min-h-[600px]">
          <div className="overflow-x-auto">
            <table className="w-full text-left min-w-[800px]">
              <tbody className="divide-y divide-slate-50">
                {filteredRecords.length > 0 ? filteredRecords.map((record) => {
                  const student = students.find(s => s.id === record.studentId);

                  const statusStyles = {
                    [AttendanceStatus.PRESENT]: "bg-emerald-50 text-emerald-600",
                    [AttendanceStatus.ABSENT]: "bg-rose-50 text-rose-600",
                    [AttendanceStatus.OD]: "bg-blue-50 text-blue-600"
                  };

                  return (
                    <tr key={record.id} className="hover:bg-slate-50/50 transition-colors group">
                      <td className="pl-10 py-8 w-48">
                        <div className="flex flex-col">
                          <span className="text-sm font-black text-slate-800 tracking-tight">{formatDateLabel(record.date)}</span>
                          <span className="text-[10px] font-black text-blue-500 uppercase tracking-widest">{getDayName(record.date)}</span>
                        </div>
                      </td>

                      <td className="px-6 py-8">
                        <div className="flex items-center space-x-5">
                          <div className="w-10 h-10 rounded-full bg-slate-100 flex items-center justify-center text-slate-400 text-[10px] font-black shadow-inner">
                            {student?.name.charAt(0).toUpperCase()}
                          </div>
                          <div className="flex flex-col">
                            <span className="text-sm font-black text-slate-800 tracking-tight">{student?.name}</span>
                            <span className="text-[10px] font-mono font-bold text-slate-400 uppercase tracking-tighter">{student?.rollNumber}</span>
                          </div>
                        </div>
                      </td>

                      <td className="px-6 py-8">
                        <div className="flex flex-col">
                          <span className="text-xs font-black text-slate-800 tracking-tight">{record.subject}</span>
                          <span className="text-[10px] font-black text-slate-300 uppercase tracking-widest">PERIOD {record.period}</span>
                        </div>
                      </td>

                      <td className="px-6 py-8 text-center">
                        <div className={`inline-flex px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest ${statusStyles[record.status]}`}>
                          {record.status}
                        </div>
                      </td>

                      <td className="pr-10 py-8 text-right">
                        <button 
                          onClick={() => onDelete(record.id)}
                          className="p-3 text-slate-200 hover:text-rose-500 transition-all opacity-0 group-hover:opacity-100"
                        >
                          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"></path></svg>
                        </button>
                      </td>
                    </tr>
                  );
                }) : (
                  <tr>
                    <td className="py-48 text-center">
                      <div className="flex flex-col items-center">
                        <div className="w-20 h-20 bg-slate-50 rounded-full flex items-center justify-center text-slate-200 mb-6">
                           <svg className="w-10 h-10" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 19a2 2 0 01-2-2V7a2 2 0 012-2h4l2 2h4a2 2 0 012 2v1M5 19h14a2 2 0 002-2v-5a2 2 0 00-2-2H9l-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"></path></svg>
                        </div>
                        <p className="text-slate-400 font-black uppercase tracking-widest text-[10px]">Select an EE folder to view records</p>
                      </div>
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceHistory;

import React, { useState, useMemo } from 'react';
import { User, Student, AttendanceRecord, AttendanceStatus } from '../types';
import { SUBJECTS } from '../constants';

interface MarkAttendanceProps {
  user: User;
  students: Student[];
  onSave: (records: AttendanceRecord[]) => void;
}

const MarkAttendance: React.FC<MarkAttendanceProps> = ({ user, students, onSave }) => {
  const [selectedSubject, setSelectedSubject] = useState(SUBJECTS[0].name);
  const [selectedPeriod, setSelectedPeriod] = useState(1);
  const [attendance, setAttendance] = useState<Record<string, AttendanceStatus>>({});
  const [isSaving, setIsSaving] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<'All' | 'Present' | 'Absent' | 'OD'>('All');

  const handleStatusChange = (studentId: string, status: AttendanceStatus) => {
    setAttendance(prev => ({ ...prev, [studentId]: status }));
  };

  const handleSave = async () => {
    setIsSaving(true);
    const today = new Date().toISOString().split('T')[0];
    const newRecords: AttendanceRecord[] = students.map(student => ({
      id: Math.random().toString(36).substr(2, 9),
      studentId: student.id,
      status: attendance[student.id] || AttendanceStatus.PRESENT, // Default to Present
      date: today,
      period: selectedPeriod,
      subject: selectedSubject,
      facultyId: user.id
    }));

    await new Promise(r => setTimeout(r, 800));
    onSave(newRecords);
    setIsSaving(false);
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 4000);
  };

  const filteredStudents = useMemo(() => {
    return students.filter(s => {
      const matchesSearch = s.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
                            s.rollNumber.toLowerCase().includes(searchQuery.toLowerCase());

      const currentStatus = attendance[s.id] || AttendanceStatus.PRESENT;
      const matchesFilter = filterStatus === 'All' || 
                            (filterStatus === 'Present' && currentStatus === AttendanceStatus.PRESENT) ||
                            (filterStatus === 'Absent' && currentStatus === AttendanceStatus.ABSENT) ||
                            (filterStatus === 'OD' && currentStatus === AttendanceStatus.OD);

      return matchesSearch && matchesFilter;
    });
  }, [students, searchQuery, filterStatus, attendance]);

  const periods = [1, 2, 3, 4, 5, 6, 7];

  return (
    <div className="flex flex-col h-full bg-[#F9FAFB] animate-in fade-in duration-500 pb-20">
      <div className="flex flex-col space-y-8 mb-10 px-2">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-black text-slate-800 tracking-tight">Electrical Engineering - Sec A</h1>
            <p className="text-slate-400 text-sm font-medium">Daily attendance management</p>
          </div>
          <button 
            onClick={handleSave}
            disabled={isSaving}
            className="bg-[#3B82F6] text-white px-10 py-3.5 rounded-2xl font-bold text-xs uppercase tracking-widest flex items-center space-x-3 hover:bg-blue-700 shadow-xl shadow-blue-100 transition-all active:scale-95 disabled:opacity-50"
          >
            {isSaving ? <span className="animate-spin text-lg">üåÄ</span> : <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20"><path d="M7.707 10.293a1 1 0 10-1.414 1.414l3 3a1 1 0 001.414 0l3-3a1 1 0 00-1.414-1.414L11 11.586V4a1 1 0 10-2 0v7.586l-1.293-1.293z"></path><path d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z"></path></svg>}
            <span>Save Changes</span>
          </button>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          <div className="bg-white p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col space-y-6">
            <span className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Select Period</span>
            <div className="flex items-center space-x-3 overflow-x-auto pb-2 scrollbar-hide">
              {periods.map((p) => (
                <button
                  key={p}
                  onClick={() => setSelectedPeriod(p)}
                  className={`flex-shrink-0 w-12 h-12 rounded-2xl flex items-center justify-center font-black transition-all border-2 ${
                    selectedPeriod === p 
                    ? 'bg-blue-600 border-blue-600 text-white shadow-xl shadow-blue-100' 
                    : 'bg-slate-50 border-transparent text-slate-400 hover:bg-slate-100'
                  }`}
                >
                  P{p}
                </button>
              ))}
            </div>
          </div>

          <div className="bg-white p-8 rounded-[2.5rem] border border-slate-100 shadow-sm flex flex-col justify-center space-y-4">
            <span className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Current Subject</span>
            <div className="flex items-center">
              <span className="px-8 py-3 rounded-2xl font-black text-blue-600 bg-blue-50 border-2 border-blue-100 uppercase tracking-widest text-xs">
                {selectedSubject}
              </span>
            </div>
          </div>
        </div>
      </div>

      <div className="flex flex-col md:flex-row items-center justify-between mb-8 px-2 gap-6">
        <div className="relative w-full md:w-96 group">
          <span className="absolute inset-y-0 left-4 flex items-center text-slate-300 group-focus-within:text-blue-500 transition-colors">
            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z"></path></svg>
          </span>
          <input 
            type="text" 
            placeholder="Search by name or roll no..." 
            className="w-full pl-12 pr-6 py-4 bg-white border border-slate-200 rounded-[1.25rem] text-sm font-medium focus:ring-4 focus:ring-blue-500/10 focus:border-blue-500 outline-none shadow-sm transition-all placeholder-slate-300"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <div className="bg-[#f1f5f9]/60 p-1.5 rounded-[1.5rem] flex items-center shadow-inner overflow-x-auto max-w-full">
          <button 
            onClick={() => setFilterStatus('All')}
            className={`px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all flex-shrink-0 ${filterStatus === 'All' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-500'}`}
          >
            All ({students.length})
          </button>
          <button 
            onClick={() => setFilterStatus('Present')}
            className={`px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all flex-shrink-0 ${filterStatus === 'Present' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-500'}`}
          >
            Present
          </button>
          <button 
            onClick={() => setFilterStatus('Absent')}
            className={`px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all flex-shrink-0 ${filterStatus === 'Absent' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-500'}`}
          >
            Absent
          </button>
          <button 
            onClick={() => setFilterStatus('OD')}
            className={`px-8 py-2.5 rounded-xl text-[10px] font-black uppercase tracking-widest transition-all flex-shrink-0 ${filterStatus === 'OD' ? 'bg-white text-slate-800 shadow-sm' : 'text-slate-400 hover:text-slate-500'}`}
          >
            OD
          </button>
        </div>
      </div>

      <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm overflow-hidden mb-12">
        <div className="overflow-x-auto">
          <table className="w-full text-left table-auto">
            <thead className="bg-slate-50/50">
              <tr className="border-b border-slate-100">
                <th className="px-12 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em] w-1/4">Roll No</th>
                <th className="px-12 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em] w-1/3">Student Name</th>
                <th className="px-12 py-8 text-[11px] font-black text-slate-400 uppercase tracking-[0.2em] text-center">Attendance Toggle</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-slate-50">
              {filteredStudents.map((student) => {
                const status = attendance[student.id] || AttendanceStatus.PRESENT;

                return (
                  <tr key={student.id} className="transition-colors group hover:bg-slate-50/30">
                    <td className="px-12 py-7">
                      <span className="text-sm font-bold text-slate-600 font-mono">{student.rollNumber}</span>
                    </td>
                    <td className="px-12 py-7">
                      <div className="flex items-center space-x-6">
                        <div className="w-10 h-10 rounded-2xl bg-[#f1f5f9] flex items-center justify-center text-slate-400 text-xs font-black shadow-inner flex-shrink-0">
                          {student.name.charAt(0)}
                        </div>
                        <span className="text-sm font-black text-slate-800 tracking-tight whitespace-nowrap overflow-hidden text-ellipsis">
                          {student.name}
                        </span>
                      </div>
                    </td>
                    <td className="px-12 py-7">
                      <div className="flex justify-center items-center space-x-4">
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.PRESENT)}
                          className={`min-w-[120px] py-3.5 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.PRESENT 
                            ? 'bg-[#10B981] border-[#10B981] text-white shadow-xl shadow-emerald-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200 hover:text-slate-400'
                          }`}
                        >
                          PRESENT
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.ABSENT)}
                          className={`min-w-[120px] py-3.5 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.ABSENT 
                            ? 'bg-[#EF4444] border-[#EF4444] text-white shadow-xl shadow-rose-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200 hover:text-slate-400'
                          }`}
                        >
                          ABSENT
                        </button>
                        <button
                          onClick={() => handleStatusChange(student.id, AttendanceStatus.OD)}
                          className={`min-w-[120px] py-3.5 rounded-2xl flex items-center justify-center text-[10px] font-black border-2 transition-all ${
                            status === AttendanceStatus.OD 
                            ? 'bg-[#3B82F6] border-[#3B82F6] text-white shadow-xl shadow-blue-100' 
                            : 'bg-white border-slate-100 text-slate-300 hover:border-slate-200 hover:text-slate-400'
                          }`}
                        >
                          OD
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
        {filteredStudents.length === 0 && (
          <div className="py-32 text-center">
            <div className="w-20 h-20 bg-slate-50 rounded-[2rem] flex items-center justify-center text-slate-200 mx-auto mb-6">
              <svg className="w-10 h-10" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z"></path></svg>
            </div>
            <p className="text-slate-400 font-black uppercase tracking-widest text-xs">No students found matching your search</p>
          </div>
        )}
      </div>

      {showSuccess && (
        <div className="fixed bottom-10 right-10 animate-in slide-in-from-right-8 duration-500 z-[70]">
          <div className="bg-[#0F172A] text-white px-10 py-6 rounded-[2.5rem] shadow-2xl flex items-center space-x-6 border border-white/10">
            <div className="w-12 h-12 bg-[#10B981] rounded-2xl flex items-center justify-center shadow-lg shadow-emerald-900/50">
              <svg className="w-7 h-7 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M5 13l4 4L19 7"></path></svg>
            </div>
            <div>
              <p className="font-black tracking-tight text-lg">EE Attendance Saved</p>
              <p className="text-slate-400 text-xs font-bold uppercase tracking-widest">Database Sync Complete</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
